interface.definition:
a!localVariables(
  local!questionsFromSelectedCategory: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
    cdt: ri!questionnaireTemplate.questions,
    field: "questionCategoryTemplateId",
    value: ri!selectedCategory.questionCategoryTemplateId
  ),
  local!questionsWithoutPrecedentSorted: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75720"(
    cdt: #"SYSTEM_SYSRULES_forEach"(
      items: local!questionsFromSelectedCategory,
      expression: if(
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(
          fv!item.isDisplayedPrecedentSets
        ),
        fv!item,
        {}
      )
    ),
    field: "displayOrder"
  ),
  local!expandAll: a!refreshVariable(
    value: false,
    refreshOnVarChange: {
      ri!selectedCategory,
      ri!triggerRefresh
    }
  ),
  {
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      align: "RIGHT",
      value: {
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: if(
            local!expandAll,
            #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
              i18nData: ri!i18nData,
              bundleKey: "AS.QNM.QuestionnaireSettings.lbl_CollapseAllQuestions"
            ),
            #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
              i18nData: ri!i18nData,
              bundleKey: "AS.QNM.QuestionnaireSettings.lbl_ExpandAllQuestions"
            )
          ),
          linkStyle: "STANDALONE",
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            saveInto: {
              a!save(
                local!expandAll,
                not(
                  local!expandAll
                )
              )
            }
          )
        )
      },
      showWhen: length(
        local!questionsFromSelectedCategory
      ) <> length(
        local!questionsWithoutPrecedentSorted
      )
    ),
    #"SYSTEM_SYSRULES_forEach"(
      items: local!questionsWithoutPrecedentSorted,
      expression: #"SYSTEM_SYSRULES_sectionLayout_v1"(
        divider: "ABOVE",
        marginbelow: "NONE",
        contents: {
          #"da6cc071-ce07-47a2-ad31-c5ec4a890a4e"(
            allQuestions: local!questionsFromSelectedCategory,
            currentQuestion: fv!item,
            i18nData: ri!i18nData,
            iterationLevel: 0,
            expandAll: local!expandAll
          )
        }
      )
    )
  }
)
