rule.definition:
/*
For the example in the comments, pretend Tasks A & B are precedents for Task C, and Task A was just completed
*/
a!localVariables(
  /*First retrieve all task IDs for which the current task was a precedent*/
  /*e.g. Retrieve the ID of Task C, since Task A was just completed*/
  local!taskIdsToPossiblyStart: #"_a-0000e3c8-6861-8000-9ba2-011c48011c48_115660"(taskId_precedent: ri!currentTask.taskId).taskId,
  /*Early exit if the current task was not a precedent to anything*/
  if(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(local!taskIdsToPossiblyStart),
    {},
    a!localVariables(
      /*Then retrieve the actual task objects for which the current task was a precedent and are in queued status*/
      /*e.g. Retrieve the Task C object, since Task A was just completed*/
      local!tasksToPossiblyStart: #"_a-0000e393-fbf1-8000-9ba2-011c48011c48_104148"(
        taskId: local!taskIdsToPossiblyStart,
        taskStatusIds: #"_a-0000e460-9d93-8000-9bc1-011c48011c48_603230",
        returnType: #"_a-0000e415-3035-8000-9bb2-011c48011c48_290163"
      ),
      if(
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(local!tasksToPossiblyStart),
        {},
        a!localVariables(
          /*Then retrieve the task objects of all preceding tasks for the possible tasks to start*/
          /*e.g. Retrieve the Task B object, since Task B was also a precedent to Task C*/
          local!precedentTasksOfThePossibleTasksToStart: #"_a-0000e393-fbf1-8000-9ba2-011c48011c48_104148"(
            taskId: tointeger(
              #"SYSTEM_SYSRULES_forEach"(
                items: local!tasksToPossiblyStart,
                expression: fv!item.taskPrecedents.taskId_precedent
              )
            ),
            returnType: #"_a-0000e415-3035-8000-9bb2-011c48011c48_290163"
          ),
          /*Then loop over each task to possibly start, and only return it if all of its precedents are completed*/
          /*e.g. We're checking that Task B is also completed, since it's a precedent of Task C*/
          local!tasksToStart: a!flatten(
            #"SYSTEM_SYSRULES_forEach"(
              items: local!tasksToPossiblyStart,
              expression: a!localVariables(
                /*Gather the tasks which we need to check are complete for this specific task*/
                /*e.g. This would return Task B, since we are checking if we can start Task C*/
                local!tasksToEnsureAreComplete: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
                  cdt: local!precedentTasksOfThePossibleTasksToStart,
                  field: "taskId",
                  value: fv!item.taskPrecedents.taskId_precedent
                ),
                /*Returns true if all precedents are Completed/Not Needed/Cancelled and not uin list to skip start*/
                /* if all precedents are Completed/Not Needed/Cancelled, but the task precedent id is present in list to skip start, returns false*/
                /*e.g. This would return True if Task B is Completed*/
                local!allPrecedentsAreComplete: and(
                  #"SYSTEM_SYSRULES_forEach"(
                    items: local!tasksToEnsureAreComplete,
                    expression: and(
                      contains(
                        {
                          #"_a-0000e460-9d93-8000-9bc1-011c48011c48_603248",
                          #"_a-0000e460-9d93-8000-9bc1-011c48011c48_603254",
                          #"_a-0000e460-9d93-8000-9bc1-011c48011c48_603260"
                        },
                        fv!item.taskStatus.taskStatusId
                      ),
                      not(
                        contains(
                          ri!taskPrecedentIdsToSkipStart,
                          fv!item.taskId
                        )
                      )
                    )
                  )
                ),
                /*Return the task object if it is ready to be started*/
                /*e.g. This will return Task C if Task B is completed, otherwise it will return nothing*/
                if(
                  local!allPrecedentsAreComplete,
                  fv!item,
                  {}
                )
              )
            )
          ),
          /*Finally, set the tasks to start as assigned*/
          /*e.g. This would set Task C to assigned*/
          #"8c731acd-3941-41e1-b06f-452fd89ab48b"(tasks: local!tasksToStart)
        )
      )
    )
  )
)
