rule.definition:
a!localVariables(

  /*Return a list of invalid values from the inputs*/
  local!invalidValues: touniformstring(#"SYSTEM_SYSRULES_forEach"(
    items: ri!dictionaryOfFieldValues,
    expression: if(
      and(
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(fv!item.mappedToValue),
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(fv!item.inputValue)
      ),
      concat(
        #"_a-0000e329-3415-8000-9ba2-011c48011c48_386641"(text: fv!item.inputValue, quote: "'"),
        " is not a valid value for ",
        #"_a-0000e329-3415-8000-9ba2-011c48011c48_386641"(text: fv!item.field, quote: "'")
      ),
      {}
    )
  )),

  /*Return an error if any values are invalid*/
  if(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(local!invalidValues), 
    #"_a-0000e329-3415-8000-9ba2-011c48011c48_385187"(invalidValues: local!invalidValues),
    {}
  )
)

