interface.definition:
a!localVariables(
  /*Check to see if there is a different category with the same name in the DB*/
  local!isDuplicateTaskCategoryName: a!refreshVariable(
    value: #"_a-0000e3d1-f4dc-8000-9ba4-011c48011c48_135350"(taskCategory: ri!taskCategory),
    /*Don't want to update every time anything changes, just the name*/
    refreshOnReferencedVarChange: false,
    refreshOnVarChange: ri!taskCategory.categoryName
  ),
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73562"(
              label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                i18nData: ri!i18nData,
                bundleKey: "AS.CO.AllBundles.lbl_CategoryName"
              ),
              required: true,
              value: ri!taskCategory.categoryName,
              validations: {
                #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_74104"(
                  value: ri!taskCategory.categoryName,
                  minLength: 1,
                  maxLength: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73500"
                ),
                if(
                  local!isDuplicateTaskCategoryName,
                  #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                    i18nData: ri!i18nData,
                    bundleKey: "AS.CO.AllBundles.vld_DuplicateCategoryName"
                  ),
                  ""
                )
              },
              saveInto: a!save(
                target: ri!taskCategory.categoryName,
                value: save!value
              ),
              refreshAfter: "UNFOCUS",
              labelPosition: "ABOVE"
            )
          },
          width: "MEDIUM_PLUS"
        )
      }
    )
  }
)
