rule.definition:
a!localVariables(
  /*sheetNumber and hasHeaders can be adapted to be rule inputs if that flexibility is desired*/
  local!sheetNumber: 0,
  local!hasHeaders: true,
  local!excelData: readexcelimportfile(
    ri!excelDocument,
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73980"(
      nullableValue: local!sheetNumber,
      replacementValue: 0
    ),
    0,
    ri!numberOfColumns
  ),
  if(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75636"(local!excelData.success),
    #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_101148"(),
    a!localVariables(
      local!headers: #"SYSTEM_SYSRULES_forEach"(
        items: index(local!excelData.result, 1, "values", {}),
        expression: if(
          local!hasHeaders,
          fv!item,
          concat("col", fv!index)
        )
      ),
      {
        sheetNumber: local!sheetNumber,
        headers: touniformstring(local!headers),
        data: if(
          #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(local!excelData.result),
          #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_101176"(),
          #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76367"(
            array: #"SYSTEM_SYSRULES_forEach"(
              items: local!excelData.result.values,
              expression: if(
                and(local!hasHeaders, fv!isFirst),
                null,
                #"_a-0000e40a-de32-8000-9ba8-011c48011c48_260165"(
                  cdt: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_101148"(),
                  fields: local!headers,
                  newValues: fv!item
                )
              )
            )
          )
        )
      }
    )
  )
)
