rule.definition:
a!localVariables(
  /*Gather all aggregated data from the two sources*/
  local!aggregatedData: {
    taskChanges: #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_248759"(
      dateFilter: ri!dateFilter
    ),
    templateChanges: #"f2a7d2f9-e3e6-4a11-a629-350daf8adfc4"(
      dateFilter: ri!dateFilter
    )
  },

  /*Get all distinct templateIds from the result set*/
  local!distinctTemplateIds: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73758"({
    tointeger(local!aggregatedData.taskChanges.templateId),
    tointeger(local!aggregatedData.templateChanges.templateId),
  }),

  /*Calcuate the series data for each template ID*/
  local!calculatedSeriesData: #"SYSTEM_SYSRULES_forEach"(
    items: local!distinctTemplateIds,
    expression: a!localVariables(
      local!templateId: fv!item,
      /*Within each templateId, calculate the series data for each config*/
      local!seriesData: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73856"(
        fieldValuePairs: #"SYSTEM_SYSRULES_forEach"(
          items: #"_a-0000e40a-961e-8000-9ba7-011c48011c48_258082"(),
          expression: {
            field: fv!item.key,
            value: #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_251642"(
              templateId: local!templateId,
              seriesConfig: fv!item,
              aggregatedData: local!aggregatedData
            )
          }
        )
      ),
      {
        templateId: local!templateId,
        seriesData: local!seriesData,
        /*Sum the total count for sorting of the templates*/
        totalCount: sum(#"SYSTEM_SYSRULES_forEach"(
          items: #"_a-0000e40a-961e-8000-9ba7-011c48011c48_258082"(),
          expression: local!seriesData[fv!item.key].count
        )),
      }
    )
  ),

  /*Sort the templates by total count*/
  #"_a-0000e415-3035-8000-9bb2-011c48011c48_292117"(
    value: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75720"(
      cdt: local!calculatedSeriesData,
      field: "totalCount",
      ascending: false
    )
  )
)
