rule.definition:
a!localVariables(
  /*Gather all data from the two sources*/
  local!taskChanges: #"_a-0000e40a-de32-8000-9ba8-011c48011c48_277014"(
    dateFilter: ri!dateFilter,
    drilldownSelection: ri!drilldownSelection,
    pagingInfo: ri!pagingInfo
  ),
  local!templateChanges: #"_a-0000e40a-de32-8000-9ba8-011c48011c48_277020"(
    dateFilter: ri!dateFilter,
    drilldownSelection: ri!drilldownSelection,
    pagingInfo: ri!pagingInfo
  ),

  /*Get the series configs for each type of change*/
  local!taskSeriesConfigs: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
    cdt: #"_a-0000e40a-961e-8000-9ba7-011c48011c48_258082"(),
    field: "dataField",
    value: "taskChanges"
  ),
  local!templateSeriesConfigs: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
    cdt: #"_a-0000e40a-961e-8000-9ba7-011c48011c48_258082"(),
    field: "dataField",
    value: "templateChanges"
  ),

  /*Join the two datasets*/
  local!joinedData: a!flatten(
    {
      #"SYSTEM_SYSRULES_forEach"(
        items: local!taskChanges,
        expression: {
          itemId: fv!item.taskProcSetupAuditId,
          timestamp: fv!item.timestamp,
          username: fv!item.username,
          auditActionCode: fv!item.auditActionCode,
          templateId: fv!item.templateId,
          seriesConfig: #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_187421"(
            value: fv!item.auditActionCode,
            inArray: local!taskSeriesConfigs.auditActionCode,
            replacement: local!taskSeriesConfigs
          ),
          auditItem: #"_a-0000e40a-de32-8000-9ba8-011c48011c48_279147"(
            value: fv!item
          )
        }
      ),
      #"SYSTEM_SYSRULES_forEach"(
        items: local!templateChanges,
        expression: {
          itemId: fv!item.templateProcSetupAuditId,
          timestamp: fv!item.timestamp,
          username: fv!item.username,
          auditActionCode: fv!item.templateAuditActionCode,
          templateId: fv!item.originalTemplateId,
          seriesConfig: #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_187421"(
            value: fv!item.templateAuditActionCode,
            inArray: local!templateSeriesConfigs.auditActionCode,
            replacement: local!templateSeriesConfigs
          ),
          auditItem: #"_a-0000e40a-de32-8000-9ba8-011c48011c48_279147"(
            value: fv!item
          )
        }
      )
    }
  ),

  /*Format the datasubset from the joined data*/
  local!datasubset: todatasubset(
    local!joinedData,
    ri!pagingInfo
  ),

  local!datasubset
)
