interface.definition:
a!localVariables(
  local!today: ri!date = today(),
  local!yesterday: ri!date = today()-1,
  local!friendlyDateBundleKey: 
    if(#"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!prefixTextBeforeDate),
      /*BundleKeys without prefix arguments*/
      if(
        local!today,
        "AS.CO.CommonObjects.lbl_TodayByUser",
        if(
          local!yesterday,
          "AS.CO.CommonObjects.lbl_YesterdayByUser",
          "AS.CO.CommonObjects.lbl_ByUser"
        )
      ),
      /*BundleKeys with prefix arguments*/
      if(
        local!today,
        "AS.CO.CommonObjects.lbl_PrefixTodayByUser",
        if(
          local!yesterday,
          "AS.CO.CommonObjects.lbl_PrefixYesterdayByUser",
          "AS.CO.CommonObjects.lbl_PrefixByUser"
        )
      )
    ),
  local!friendlyDateRichText: #"_a-0000e471-623a-8000-9bc3-011c48011c48_702641"(
    i18nData: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81720"(
      bundleFolder: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_104931"
    ),
    bundleKey: local!friendlyDateBundleKey,
    richTextArguments: if(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!prefixTextBeforeDate),
      {
        /*richTextItem*/
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75762"(
          username: ri!username,
          valueIfNull: ""
        )
      },
      {
        /*prefix text*/
        ri!prefixTextBeforeDate,
        /*richTextItem*/
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75762"(
          username: ri!username,
          valueIfNull: ""
        )
      }
      )
  ),
  #"SYSTEM_SYSRULES_richTextDisplayField"(
    label: ri!label,
    labelPosition: ri!labelPosition,
    value: if(
      or(
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(
          ri!username
        ),
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(
          ri!date
        )
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "-"
      ),
      {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text:{ri!date, " ", local!friendlyDateRichText},
        showWhen: not(or(local!today, local!yesterday))
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: local!friendlyDateRichText,
        showWhen: or(local!today, local!yesterday)        
      )  
      }
  )
)
)
