rule.definition:
/*Rule returns true if SLA days of tasks match its precedent tasks else false if SLA is unrealistic*/
if(
  or(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!currentTask.templateTaskPrecedents),
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!currentTask.slaDays)
  ),
  /*if sla days or precedent tasks are not configured returns true */
  true,
  a!localVariables(
    local!precedentTasks: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
      cdt: ri!allTasks,
      field: "templateTaskUUID",
      value: {
        ri!currentTask.templateTaskPrecedents.templateTaskUUIDPrecedent
      }
    ),
    if(
      ri!currentTask.slaRule = #"_a-0000e460-9d93-8000-9bc1-011c48011c48_620337",
      {
        and(
          #"SYSTEM_SYSRULES_forEach"(
            items: local!precedentTasks,
            expression: if(
              #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(fv!item.slaDays),
              true,
              if(
                fv!item.slaRule = #"_a-0000e460-9d93-8000-9bc1-011c48011c48_620337",
                /*check if SLA days of current task is less than or equal to precedent tasks SLA */
                ri!currentTask.slaDays <= fv!item.slaDays,
                /*if current tasks SLA is before est funding date and precedent task's is after est funding date return false */
                false
              )
            )
          )
        )
      },
      {
        and(
          #"SYSTEM_SYSRULES_forEach"(
            items: local!precedentTasks,
            expression: if(
              #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(fv!item.slaDays),
              true,
              if(
                fv!item.slaRule = #"_a-0000e460-9d93-8000-9bc1-011c48011c48_620337",
                /*if current tasks SLA is after est funding date and precedent task's is before est funding date return true */
                true,
                /*check if SLA days of current task is greater than or equal to precedent tasks SLA */
                ri!currentTask.slaDays >= fv!item.slaDays
              )
            )
          )
        )
      }
    )
  )
)

a:ruleInputTestConfigs.a:value:
{
  slaRule:#"_a-0000e460-9d93-8000-9bc1-011c48011c48_620337",
  slaDays:3,
  templateTaskPrecedents:{
    {
      taskRefId_precedent:1
    },
    {
      taskRefId_precedent:2
    },
    {
      taskRefId_precedent:3
    },
    {
      taskRefId_precedent: 4
    }
  }  
}

