interface.definition:
a!localVariables(
  /*display as add/delete if from/to value is blank*/
  local!auditActionCode: if(
    /*if this is a document that has been added, want audit to say 'uploaded' instead of 'added' */
    and(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(
        ri!onboardingRequestAuditEntry.onboardingDocumentId
      ),
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(
        ri!onboardingRequestAuditEntry.oldValue
      ),
      ri!onboardingRequestAuditEntry.auditActionCode = #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_181584-adt"
    ),
    #"_a-0000e79b-0335-8000-9c0c-011c48011c48_2939008",
    ri!onboardingRequestAuditEntry.auditActionCode
  ),
  #"SYSTEM_SYSRULES_richTextDisplayField"(
    value: {
      /*Added, Edited, or Deleted*/
      #"_a-0000e471-623a-8000-9bc3-011c48011c48_702641"(
        i18nData: ri!i18nData,
        bundleKey: #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_187421"(
          value: local!auditActionCode,
          inArray: {
            #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_181584-adt",
            #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_181596-adt",
            #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_181590-adt",
            #"_a-0000e79b-0335-8000-9c0c-011c48011c48_2939008"
          },
          replacement: {
            "AS.CO.AllBundles.txt_AddedValueDate",
            "AS.CO.AllBundles.txt_Edited",
            "AS.CO.AllBundles.txt_DeletedValueDate",
            "AS.CO.AllBundles.txt_Uploaded"
          },
          customDefault: ""
        ),
        richTextArguments: {
          #"_a-0000e471-623a-8000-9bc3-011c48011c48_761219"(
            i18nData: ri!i18nData,
            onbRequestAuditEntry: ri!onboardingRequestAuditEntry
          ),
          {
            #"_a-0000e40a-de32-8000-9ba8-011c48011c48_280511-adt"(
              displayText: if(
                ri!onboardingRequestAuditEntry.fieldName = "isDeleted",
                /* customer doument is nullified when doc is deleted, so the doc name is null.*/
                /* So to display doc name for deleted docs, fetching it from add audit entry of the same doc*/
                if(
                  #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(
                    ri!onboardingRequestAuditEntry.onboardingDocumentId
                  ),
                  a!localVariables(
                    local!auditdetails: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
                      cdt: ri!allOnboardingDocAudits,
                      field: "onboardingDocumentId",
                      value: ri!onboardingRequestAuditEntry.onboardingDocumentId
                    ),
                    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76367"(
                      index(
                        local!auditdetails,
                        wherecontains(
                          touniformstring("customerDocument.documentName"),
                          touniformstring(local!auditdetails.fieldName),

                        ),
                        null
                      ).newValue
                    )
                  ),
                  ri!requirementName
                ),
                ri!onboardingRequestAuditEntry.newValue
              ),
              textStyle: "STRONG"
            )
          }
        }
      ),
      #"SYSTEM_SYSRULES_richTextBulletedList"(
        items: 
          {
            /*to display the funding details change reason*/
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                i18nData: ri!i18nData,
                bundleKey: "AS.CO.AllBundles.txt_ChangeReason",
                arguments: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
                  cdt: ri!changeReasons,
                  field: "code",
                  value: ri!onboardingRequestAuditEntry.comment
                ).label
              ),
              size: "SMALL",
              showWhen: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(ri!onboardingRequestAuditEntry.comment)
            ),
            /*to display the Requirement doc upload details*/
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: #"_a-0000e471-623a-8000-9bc3-011c48011c48_702641"(
                i18nData: ri!i18nData,
                bundleKey: "AS.CO.AllBundles.txt_ForDocRequirement",
                richTextArguments: #"SYSTEM_SYSRULES_richTextItem_v1"(text: ri!requirementName, style: "STRONG")
              ),
              showWhen: and(
                local!auditActionCode = #"_a-0000e79b-0335-8000-9c0c-011c48011c48_2939008",
                #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(ri!requirementName)
              )
            ),
            /*to display the edited document details*/
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: #"_a-0000e471-623a-8000-9bc3-011c48011c48_702641"(
                i18nData: ri!i18nData,
                bundleKey: "AS.CO.AllBundles.txt_EditedValueFromDateTo",
                richTextArguments: {
                  choose(
                    #"_a-0000e40a-de32-8000-9ba8-011c48011c48_280802"(
                      value: ri!onboardingRequestAuditEntry.fieldName,
                      inArray: {
                        #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3233455"()
                      }
                    ),
                    /*Default*/
                    {},
                    /*security*/
                    #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                        i18nData: ri!i18nData,
                        bundleKey: "AS.CO.AllBundles.txt_InternalViewersOnly"
                      )
                    ),
                    /*expiration date*/
                    #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                        i18nData: ri!i18nData,
                        bundleKey: "AS.CO.AllBundles.lbl_ExpirationDate"
                      )
                    ),
                    /*description*/
                    #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                        i18nData: ri!i18nData,
                        bundleKey: "AS.CO.AllBundles.lbl_documentDescription"
                      )
                    )
                  ),
                  #"_a-0000e40a-de32-8000-9ba8-011c48011c48_280511-adt"(
                    displayText: ri!onboardingRequestAuditEntry.oldValue,
                    textStyle: "STRONG"
                  ),
                  #"_a-0000e40a-de32-8000-9ba8-011c48011c48_280511-adt"(
                    displayText: ri!onboardingRequestAuditEntry.newValue,
                    textStyle: "STRONG"
                  )
                }
              ),
              showWhen: and(
                local!auditActionCode = #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_181596-adt",
                contains(
                  {
                    "customerDocument.isInternal",
                    "customerDocument.expirationDate",
                    "customerDocument.documentDesc"
                  },
                  ri!onboardingRequestAuditEntry.fieldName
                )
              )
            ),
            #"SYSTEM_SYSRULES_richTextListItem"(
              text: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  size: "SMALL",
                  text: concat(
                    #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                      i18nData: ri!i18nData,
                      bundleKey: if(
                        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(
                          ri!onboardingRequestAuditEntry.oldValue
                        ),
                        "AS.CO.AllBundles.txt_Blank",
                        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
                          cdt: ri!refData,
                          field: "code",
                          value: ri!onboardingRequestAuditEntry.oldValue
                        ).label
                      ),

                    ),
                    " "
                  )
                ),
                #"SYSTEM_SYSRULES_richTextIcon"(icon: "arrow-right", size: "SMALL"),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  size: "SMALL",
                  text: concat(
                    " ",
                    #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                      i18nData: ri!i18nData,
                      bundleKey: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
                        cdt: ri!refData,
                        field: "code",
                        value: ri!onboardingRequestAuditEntry.newValue
                      ).label
                    )
                  )
                )
              },
              showWhen: ri!onboardingRequestAuditEntry.fieldName = "status"
            )
          }

      )
    }
  )
)
