interface.definition:
a!localVariables(
  local!initialNumTasks: 50,
  local!numTasksToShow: local!initialNumTasks,
  local!sortedHierarchyTasks: #"_a-0000e776-697a-8000-9c05-011c48011c48_2827461"(
    precedentTask: null,
    /*replace a null list with null since null list breaks recursion*/
    allTasks: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73980"(
      nullableValue: ri!allTasks,
      replacementValue: null
    ),
    level: 0
  ),
  local!totalTasksShownForEachTopTask: if(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(local!sortedHierarchyTasks.dependents),
    /*if there are no tasks, return an empty list*/
    tointeger({}),
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        /*for each top level task*/
        items: enumerate(
          #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_880586"(local!sortedHierarchyTasks.dependents)
        ) + 1,
        /*find how many tasks would be shown if that task and all of its dependents*/
        /*were shown, as well as any previous tasks*/
        expression: a!localVariables(
          /*find the sum of the current and previous dependent tasks*/
          local!totalTasks: sum(
            #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76433"(
              cdt: index(
                local!sortedHierarchyTasks.dependents,
                enumerate(fv!item) + 1,
                {}
              ),
              field: "numTotalDependents",
              indexDefault: 0
            ) + 1
          ),
          /*if the number of tasks would be above the limit, return {}, else the number of tasks*/
          if(
            or(
              local!numTasksToShow = - 1,
              local!numTasksToShow >= local!totalTasks
            ),
            local!totalTasks,
            {}
          )
        )
      )
    )
  ),
  {
    #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3312448"("AS_IO_CPS_runtimetaskTimeLine"),
    #"SYSTEM_SYSRULES_forEach"(
      items: local!totalTasksShownForEachTopTask,
      expression: #"SYSTEM_SYSRULES_sectionLayout_v1"(
        divider: if(fv!isFirst, "NONE", "ABOVE"),
        marginbelow: "NONE",
        contents: #"_a-0000e4ae-1ed6-8000-9bc5-011c48011c48_802721"(
          iterationLevel: 0,
          currentTask: local!sortedHierarchyTasks.dependents[fv!index].task,
          dependents: local!sortedHierarchyTasks.dependents[fv!index].dependents,
          i18nData: ri!i18nData
        )
      )
    ),
    #"_a-0000e62c-7375-8000-9bec-011c48011c48_1757926"(
      align: "LEFT",
      i18nData: ri!i18nData,
      batchSize: local!numTasksToShow,
      numItems: local!sortedHierarchyTasks.numTotalDependents,
      collapsedBatchSize: local!initialNumTasks
    )
  }
)

