interface.definition:
a!localVariables(
  local!possiblePrecedentQuestions: a!refreshVariable(
    value: #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_872090"(
      question: ri!question,
      allQuestions: ri!questionnaireTemplate.questions
    ),
    refreshAlways: true
  ),
  local!isFollowup: a!refreshVariable(
    value: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
        cdt: ri!question.isDisplayedPrecedentSets,
        field: "fieldKey",
        value: null
      )
    ),
    refreshOnReferencedVarChange: false
  ),
  /* We track the displayPrecedentSet separately from the ri so that it is not lost if the followup checkbox is unselected 
  Currently this is only set up to support a simple, single question response requirement because this is easier
  The CDT structure supports more complicated conditions, but this would require some heavy lifting here in the UX */
  local!questionDisplayPrecedentSet: a!refreshVariable(
    value: index(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
        cdt: ri!question.isDisplayedPrecedentSets,
        field: "fieldKey",
        value: null
      ),
      1,
      null
    ),
    refreshOnReferencedVarChange: true
  ),
  /* Must be executed after each save in this interface to persist the local into the ri */
  local!saveDisplayedPrecedent: a!refreshVariable(
    value: a!save(
      ri!question.isDisplayedPrecedentSets,
      /* Only replace question level display precedents -- field level display is controlled within the question and handled separately */
      if(
        toboolean(local!isFollowup) = true,
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76367"(
          append(
            #"_a-0000e40a-de32-8000-9ba8-011c48011c48_271202"(
              cdt: ri!question.isDisplayedPrecedentSets,
              field: "fieldKey",
              value: null
            ),
            local!questionDisplayPrecedentSet
          )
        ),
        null
      )
    ),
    refreshAlways: true
  ),
  local!notConfigurable: a!refreshVariable(
    value: or(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(local!possiblePrecedentQuestions),
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!question.questionReferenceId)
    ),
    refreshOnVarChange: ri!question
  ),
  #"SYSTEM_SYSRULES_sectionLayout_v1"(
    label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
      i18nData: ri!i18nData,
      bundleKey: "AS.QNM.QuestionnaireSettings.lbl_ConfigureVisibility"
    ),
    contents: {
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_74506"(
        label: "",
        choiceLabels: {
          #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
            i18nData: ri!i18nData,
            bundleKey: "AS.QNM.QuestionnaireSettings.txt_StandaloneQuestion"
          ),
          #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
            i18nData: ri!i18nData,
            bundleKey: "AS.QNM.QuestionnaireSettings.txt_FollowUpQuestion"
          )
        },
        choiceValues: { false, true },
        value: local!isFollowup,
        saveInto: {
          local!isFollowup,
          local!saveDisplayedPrecedent
        },
        disabled: local!notConfigurable
      ),
      a!localVariables(
        local!selectedPrecedentQuestion: index(
          #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
            cdt: ri!questionnaireTemplate.questions,
            field: "questionTemplateId",
            value: index(
              local!questionDisplayPrecedentSet.questionPrecedents,
              "questionTemplateId_precedent",
              null
            )
          ),
          1,
          null
        ),
        #"SYSTEM_SYSRULES_columnsLayout"(
          columns: {
            #"SYSTEM_SYSRULES_columnLayout"(
              contents: {
                #"_a-0000e528-bc07-8000-9bcf-011c48011c48_1027802"(
                  label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                    i18nData: ri!i18nData,
                    bundleKey: "AS.QNM.QuestionnaireSettings.lbl_WhichQuestionFollow"
                  ),
                  placeholderLabel: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                    i18nData: ri!i18nData,
                    bundleKey: "AS.QNM.QuestionnaireSettings.lbl_Question"
                  ),
                  value: local!selectedPrecedentQuestion.questionTemplateId,
                  choiceLabels: local!possiblePrecedentQuestions.label,
                  choiceValues: local!possiblePrecedentQuestions.questionTemplateId,
                  saveInto: {
                    a!save(
                      local!questionDisplayPrecedentSet,
                      if(
                        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(save!value),
                        null,
                        'type!{urn:com:appian:types:AS:QNM}AS_QNM_T_QuestionPrecedentSet'(
                          operator: "AND",
                          questionPrecedents: 'type!{urn:com:appian:types:AS:QNM}AS_QNM_T_QuestionPrecedent'(
                            questionTemplateId_precedent: save!value,
                            responseRequirement: 'type!{urn:com:appian:types:AS:QNM}AS_QNM_T_ResponseRequirement'()
                          )
                        )
                      )
                    ),
                    local!saveDisplayedPrecedent
                  },
                  required: true,
                  validations: {
                    #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_866716"(
                      selectedQuestion: ri!question,
                      allQuestions: ri!questionnaireTemplate.questions,
                      i18nData: ri!i18nData
                    )
                  }
                )
              }
            ),
            #"SYSTEM_SYSRULES_columnLayout"(
              contents: if(
                #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(local!selectedPrecedentQuestion),
                a!localVariables(
                  local!selectedPrecedentQuestionResponses: local!selectedPrecedentQuestion.responseOptions,
                  local!selectedQuestionChoice: local!questionDisplayPrecedentSet.questionPrecedents[1].responseRequirement.requirementValue,
                  {
                    #"_a-0000e528-bc07-8000-9bcf-011c48011c48_1027802"(
                      label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                        i18nData: ri!i18nData,
                        bundleKey: "AS.QNM.QuestionnaireSettings.lbl_WhichChoiceSelected"
                      ),
                      placeholderLabel: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                        i18nData: ri!i18nData,
                        bundleKey: "AS.QNM.QuestionnaireSettings.lbl_Choice"
                      ),
                      value: local!selectedQuestionChoice,
                      required: true,
                      choiceLabels: local!selectedPrecedentQuestionResponses.label,
                      choiceValues: local!selectedPrecedentQuestionResponses.value,
                      saveInto: {
                        a!save(
                          local!questionDisplayPrecedentSet.questionPrecedents[1].responseRequirement,
                          'type!{urn:com:appian:types:AS:QNM}AS_QNM_T_ResponseRequirement'(
                            requirementTypeCode: #"_a-0000e471-623a-8000-9bc3-011c48011c48_671102",
                            requirementValue: save!value
                          )
                        ),
                        local!saveDisplayedPrecedent
                      }
                    )
                  }
                ),
                {}
              )
            ),
            #"SYSTEM_SYSRULES_columnLayout"(contents: {})
          },
          showWhen: and(
            local!isFollowup,
            local!notConfigurable = false
          )
        )
      )
    }
  )
)
