rule.definition:
a!localVariables(
  local!followUpQuestions: #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_873728"(
    question: ri!question,
    allQuestions: ri!allQuestions
  ),
  local!unrelatedQuestionsInCategory: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
    cdt: #"_a-0000e40a-de32-8000-9ba8-011c48011c48_271202"(
      cdt: ri!allQuestions,
      field: "questionTemplateId",
      value: append(
        ri!question.questionTemplateId,
        local!followupQuestions.questionTemplateId
      )
    ),
    field: "questionCategoryTemplateId",
    value: ri!question.questionCategoryTemplateId
  ),
  local!isQuestionDisplayedPrecedentSets: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
    cdt: ri!question.isDisplayedPrecedentSets,
    field: "fieldKey",
    value: null
  ),
  local!precedentQuestion: if(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(local!isQuestionDisplayedPrecedentSets),
    null,
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
      cdt: local!unrelatedQuestionsInCategory,
      field: "questionTemplateId",
      value: index(
        index(
          local!isQuestionDisplayedPrecedentSets[1],
          "questionPrecedents",
          null
        )[1],
        "questionTemplateId_precedent",
        null
      )
    )
  ),
/*  If not a follow-up question, returns the next group number.
    If a follow-up questions, returns the group of the precedent question it is following */
  local!newGroupNumber: if(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(
      local!precedentQuestion
    ),
    max(
      local!unrelatedQuestionsInCategory.groupNumber
    ) + 1,
    local!precedentQuestion.groupNumber
  ),
/*  use prior order number here in order to update order number by question index (see line 96) */
  local!priorOrderNumber: if(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(
      local!precedentQuestion
    ),
    0,
    max(
      append(
        local!precedentQuestion,
        #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_873728"(
          question: local!precedentQuestion,
          allQuestions: local!unrelatedQuestionsInCategory
        )
      ).orderNumber
    )
  ),
  /* Say you have a precedent structure with multiple levels of nesting in a group.
  If you set a question as a followup to the first followup question off of the top question,
  you will also need to bump down the other followup questions and their followups
  (basically, anything with a higher order number) */
  local!questionsInGroupToBeBumpedDown: a!localVariables(
    local!unrelatedQuestionsInGroup: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
      cdt: local!unrelatedQuestionsInCategory,
      field: "groupNumber",
      value: local!newGroupNumber
    ),
    index(
      local!unrelatedQuestionsInGroup,
      where(
        local!unrelatedQuestionsInGroup.orderNumber > local!priorOrderNumber
      ),
      {}
    )
  ),
  local!updatedQuestions: #"SYSTEM_SYSRULES_forEach"(
    items: append(
      ri!question,
      append(
        local!followUpQuestions,
        local!questionsInGroupToBeBumpedDown
      )
    ),
    expression: #"_a-0000e40a-de32-8000-9ba8-011c48011c48_269170"(
      cdt: fv!item,
      fieldValuePairs: {
        {
          field: "groupNumber",
          value: local!newGroupNumber
        },
        {
          field: "orderNumber",
          value: local!priorOrderNumber + fv!index
        }
      }
    )
  ),
  reduce(
    #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_882558"(
      allQuestions: _,
      question: _
    ),
    ri!allQuestions,
    local!updatedQuestions
  )
)

