interface.definition:
a!localVariables(
  local!questionFields: #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_885430"(),
  local!questionImportData: #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_885303"(
    excelDocument: ri!questionImportDocument,
    numberOfColumns: count(
      local!questionFields
    )
  ),
  /*Return an array of headers from Excel with their matching task field*/
  local!excelColumns: #"SYSTEM_SYSRULES_forEach"(
    items: local!questionImportData.headers,
    expression: {
      excelHeader: fv!item,
      questionField: index(
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
          cdt: local!questionFields,
          field: "label",
          value: fv!item
        ),
        1,
        null
      )
    }
  ),
  /*The document isn't blank and the parsing returned a valid value*/
  local!fileSuccessfullyUploaded: and(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(
      ri!questionImportDocument
    ),
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(
      local!questionImportData.data
    )
  ),
  local!importedRefQuestions: #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_885636"(
