  ),
  local!showWarningMesage: local!existingQuestionRefsCount > 0,
  local!invalidFile: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(
    difference(
      a!flatten(
        local!questionImportData.headers
      ),
      touniformstring(
        local!questionFields.label
      )
    )
  ),
  #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_933789"(
    formLabel: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
      i18nData: ri!i18nData,
      bundleKey: "AS.QNM.QuestionnaireSettings.lbl_ImportQuestions"
    ),
    primaryName: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
      i18nData: ri!i18nData,
      bundleKey: "AS.QNM.Question.lbl_UploadAnExcelFile"
    ),
    /* Let's make this a rich text bulletted list or something?  And it should pull from REF_questionTypes. */
    instructions: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
      i18nData: ri!i18nData,
      bundleKey: "AS.QNM.Question.ins_QuestionImportExcelHeaders",
      arguments: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_55491"(
        list: #"SYSTEM_SYSRULES_forEach"(
          items: local!questionFields.label,
          expression: #"_a-0000e329-3415-8000-9ba2-011c48011c48_386641"(
            text: fv!item
          )
        ),
        finalSeparator: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
          i18nData: ri!i18nData,
          bundleKey: "AS.QNM.Question.txt_And"
        )
      )
    ),
    formContents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              /*File upload field*/
              #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_74302"(
                label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                  i18nData: ri!i18nData,
                  bundleKey: "AS.QNM.Question.lbl_ExcelFile"
                ),
                target: #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_885818",
                maxSelections: 1,
                value: ri!questionImportDocument,
                saveInto: {
                  ri!questionImportDocument
                }
              ),
              /*Sample Import File*/
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                labelPosition: "COLLAPSED",
                value: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                      i18nData: ri!i18nData,
                      bundleKey: "AS.QNM.Question.ins_SampleFile"
                    ),
                    link: #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_850779"(
                      document: #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_885849"
                    ),
                    linkStyle: "STANDALONE"
                  )
                },
                showWhen: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75636"(
                  local!fileSuccessfullyUploaded
                )
              )
            }
          ),
          #"SYSTEM_SYSRULES_columnLayout"()
        }
      ),
      #"SYSTEM_SYSRULES_sectionLayout_v1"(
        contents: {
          #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_907744"(
            i18nData: ri!i18nData,
            excelColumns: local!excelColumns,
