interface.definition:
a!localVariables(
  local!showWarnIcons: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_forEach"(
      items: ri!products,
      expression: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(
        #"_a-0000e528-bc07-8000-9bcf-011c48011c48_1105066"(products: fv!item, i18nData: ri!i18nData)
      )
    ),
    refreshAlways: true
  ),
  {
    #"SYSTEM_SYSRULES_sideBySideLayout"(
      items: {
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: upper(
                #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                  i18nData: ri!i18nData,
                  bundleKey: "AS.CO.AllBundles.lbl_Products"
                )
              ),
              color: "SECONDARY",
              size: "MEDIUM",
              style: "STRONG"
            )
          )
        )
      },
      alignVertical: "MIDDLE"
    ),
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!products,
      expression: a!localVariables(
        local!isSelected: fv!index = ri!selectedProductIndex,
        local!productName: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73980"(
          nullableValue: fv!item.name,
          replacementValue: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
            i18nData: ri!i18nData,
            bundleKey: "AS.CO.AllBundles.lbl_UnnamedProductPlaceholder"
          )
        ),
        local!showWarnIcon: local!showWarnIcons[fv!index],
        {
          #"SYSTEM_SYSRULES_cardLayout"(
            contents: #"SYSTEM_SYSRULES_sideBySideLayout"(
              items: {
                #"SYSTEM_SYSRULES_sideBySideItem"(
                  item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                    labelPosition: "COLLAPSED",
                    value: {
                      if(
                        local!showWarnIcon,
                        {
                          #"SYSTEM_SYSRULES_richTextIcon"(
                            caption: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                              i18nData: ri!i18nData,
                              bundleKey: "AS.CO.AllBundles.ins_IncompleteProductDetails"
                            ),
                            icon: "warning",
                            color: "NEGATIVE"
                          ),
                          #"SYSTEM_SYSRULES_richTextItem_v1"(text: " ")
                        },
                        if(
                          or(local!showWarnIcons),
                          #"SYSTEM_SYSRULES_richTextItem_v1"(text: "     "),
                          {}
                        )
                      ),
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: local!productName,
                        color: if(local!isSelected, "STANDARD", "ACCENT"),
                        size: "MEDIUM"
                      ),
                      #"SYSTEM_SYSRULES_richTextIcon"(
                        icon: "caret-right",
                        showWhen: local!isSelected
                      )
                    }
                  )
                )
              },
              alignVertical: "MIDDLE"
            ),
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              value: fv!index,
              saveInto: ri!selectedProductIndex
            ),
            style: "NONE",
            showBorder: false,
            accessibilityText: if(
              local!isSelected,
              fv!item.name & " " & "selected",
              ""
            )
          )
        }
      )
    ),
    #"SYSTEM_SYSRULES_richTextDisplayField"(labelPosition: "COLLAPSED"),
    #"_a-0000e69f-3688-8000-9bf2-011c48011c48_2129558"(
      icon: "plus-circle",
      label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
        i18nData: ri!i18nData,
        bundleKey: "AS.CO.AllBundles.lbl_AddProduct",

      ),
      saveInto: {
        a!save(
          ri!products,
          append(
            ri!products,
            'type!{urn:com:appian:types:AS:IO}AS_IO_Product_P'()
          )
        ),
        a!save(
          ri!selectedProductIndex,
          length(ri!products)
        ),
        #"_a-0000e539-6d59-8000-9bd0-011c48011c48_1234334"()
      },
      color: "ACCENT",
      showWhen: ri!readOnly <> true
    ),
    /* This card is used to set a minimum height on the column so that the *
   * divider takes up more screen space when there is minimal content.   *
   * Once content is added to the main column, this can be removed.      */
    #"SYSTEM_SYSRULES_cardLayout"(
      height: "TALL",
      showWhen: not(#"SYSTEM_SYSRULES_isNativePhone"()),
      showBorder: false
    )
  }
)
