rule.definition:
with(
  local!stringSeparatedByCharacter: split(
    ri!string,
    ri!character
  ),
  local!separatedArrayLength: count(local!stringSeparatedByCharacter),
  local!backToBackCharacterPositions: wherecontains(
    null,
    local!stringSeparatedByCharacter
  ),
  /* 
   * numCharsToTrimFromBack: Takes the reverse of the array of positions where there are back to back instances of the
   * input character and counts the instances where this aligns with the enumeration of the length of the split array.
   * This will be true as long as there are contiguous instances of the given character at the back, but once a different
   * character is reached then the back to back character array will jump and the enumeration will desync, so no more are counted
   */
  local!numCharsToTrimFromBack: count(
    where(
      reverse(local!backToBackCharacterPositions) = reverse(enumerate(local!separatedArrayLength) + 1)
    )
  ),
  /* see numCharsToTrimFromBack description; similarly implemented for the front */
  local!numCharsToTrimFromFront: count(
    where(
      local!backToBackCharacterPositions = enumerate(local!separatedArrayLength) + 1
    )
  ),
  local!trimmedFromBack: left(ri!string, len(ri!string) - local!numCharsToTrimFromBack),
  right(local!trimmedFromBack, len(local!trimmedFromBack) - local!numCharsToTrimFromFront)
)
