interface.definition:
a!localVariables(
  local!fullSpanComponents: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(cdt: ri!allFields, field: "span", value: 4),
  /*local!span3Components: rule!AS_CO_UT_filterCdtByField(cdt: ri!allFields, field: "span", value: 3),*/
  local!span2Components: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(cdt: ri!allFields, field: "span", value: 2),
  local!span1Components: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(cdt: ri!allFields, field: "span", value: 1),
  local!lengthSpan1Components: length(local!span1Components),
  local!indicesSpan1Components: enumerate(local!lengthSpan1Components),
  local!lengthSpan2Components: length(local!span2Components),
  local!indicesSpan2Components: enumerate(local!lengthSpan2Components),
  local!column1Components: index(
    local!span1Components.components,
    where(mod(local!indicesSpan1Components, 4) = 0),
    null
  ),
  local!column2Components: index(
    local!span1Components.components,
    where(mod(local!indicesSpan1Components, 4) = 1),
    null
  ),
  local!column3Components: index(
    local!span1Components.components,
    where(mod(local!indicesSpan1Components, 4) = 2),
    null
  ),
  local!column4Components: index(
    local!span1Components.components,
    where(mod(local!indicesSpan1Components, 4) = 3),
    null
  ),
  local!column1and2Components: index(
    local!span2Components.components,
    where(mod(local!indicesSpan2Components, 2) = 0),
    null
  ),
  local!column3and4Components: index(
    local!span2Components.components,
    where(mod(local!indicesSpan2Components, 2) = 1),
    null
  ),
  /*Adjust span 1 components in the case of an odd number of span 2 components*/
  local!needsSpan1Adjustment: mod(local!lengthSpan2Components, 2) = 1,
  local!column1ComponentsAdjusted: if(
    and(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(local!column1Components),
      local!needsSpan1Adjustment
    ),
    remove(local!column1Components, 1),
    local!column1Components
  ),
  local!column2ComponentsAdjusted: if(
    and(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(local!column1Components),
      local!needsSpan1Adjustment
    ),
    remove(local!column2Components, 1),
    local!column2Components
  ),
  local!column3ComponentsAdjusted: if(
    local!needsSpan1Adjustment,
    append(
      local!column3Components,
      index(local!column1Components, 1, null)
    ),
    local!column3Components
  ),
  local!column4ComponentsAdjusted: if(
    local!needsSpan1Adjustment,
    append(
      local!column4Components,
      index(local!column2Components, 1, null)
    ),
    local!column4Components
  ),
  /*Adjust span 2 components in the case of the first two columns being longer than the second two columns after span 1 adjustment */
  local!needsSpan2Adjustment: length(local!column1ComponentsAdjusted) + length(local!column1and2Components) < length(local!column3ComponentsAdjusted) + length(local!column3and4Components),
  local!column1and2ComponentsAdjusted: if(
    local!needsSpan2Adjustment,
    append(
      local!column1and2Components,
      index(local!column3and4Components, 1, null)
    ),
    local!column1and2Components
  ),
  local!column3and4ComponentsAdjusted: if(
    and(
      local!needsSpan2Adjustment,
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(local!column3and4Components)
    ),
    remove(local!column3and4Components, 1),
    local!column3and4Components
  ),
  #"SYSTEM_SYSRULES_columnsLayout"(
    columns: {
      #"SYSTEM_SYSRULES_columnLayout"(
        /*reject null values from array of contents for each column to ensure no lists of null cause the interface to error*/
        contents: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76367"(
          {
            #"SYSTEM_SYSRULES_columnsLayout"(
              columns: {
                #"SYSTEM_SYSRULES_columnLayout"(
                  contents: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76367"(
                    {
                      #"SYSTEM_SYSRULES_columnsLayout"(
                        columns: {
                          #"SYSTEM_SYSRULES_columnLayout"(
                            contents: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76367"({ local!column1ComponentsAdjusted })
                          ),
                          #"SYSTEM_SYSRULES_columnLayout"(
                            contents: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76367"({ local!column2ComponentsAdjusted })
                          )
                        }
                      ),
                      /*Put half of the components that span 2 columns here*/
                      local!column1and2ComponentsAdjusted
                    }
                  )
                ),
                #"SYSTEM_SYSRULES_columnLayout"(
                  contents: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76367"(
                    {
                      #"SYSTEM_SYSRULES_columnsLayout"(
                        columns: {
                          #"SYSTEM_SYSRULES_columnLayout"(
                            contents: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76367"(local!column3ComponentsAdjusted)
                          ),
                          #"SYSTEM_SYSRULES_columnLayout"(
                            contents: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76367"(local!column4ComponentsAdjusted)
                          )
                        }
                      ),
                      /*Put half of the components that span 2 columns here*/
                      local!column3and4ComponentsAdjusted
                    }
                  )
                )
              },
              stackWhen: {
                "PHONE",
                "TABLET_PORTRAIT",
                "TABLET_LANDSCAPE"
              }
            ),
            /*Put things that span all the way across here */
            local!fullSpanComponents.components
          }
        )
      )
    }
  )
)

