interface.definition:
#"SYSTEM_SYSRULES_richTextDisplayField"(
  labelPosition: ri!labelPosition,
  align: ri!align,
  value: {
    #"SYSTEM_SYSRULES_richTextIcon"(
      icon: "angle-double-left",
      link: #"SYSTEM_SYSRULES_dynamicLink"(
        saveInto: {
          a!save(ri!pagingInfo.startIndex, 1),
          ri!additionalSaveInto,
          a!save(ri!pageNumber, 1)
        },
        showWhen: ri!pagingInfo.startIndex <> 1
      ),
      linkStyle: "STANDALONE",
      color: if(
        ri!pagingInfo.startIndex <> 1,
        "STANDARD",
        "SECONDARY"
      ),
      size: ri!size
    ),
    #"SYSTEM_SYSRULES_richTextIcon"(
      icon: "angle-left",
      link: #"SYSTEM_SYSRULES_dynamicLink"(
        saveInto: {
          a!save(
            ri!pagingInfo.startIndex,
            if(
              ri!pagingInfo.startIndex - ri!pagingInfo.batchSize < 1,
              1,
              ri!pagingInfo.startIndex - ri!pagingInfo.batchSize
            )
          ),
          ri!additionalSaveInto,
          a!save(ri!pageNumber, ri!pageNumber - 1)
        },
        showWhen: ri!pagingInfo.startIndex <> 1
      ),
      linkStyle: "STANDALONE",
      color: if(
        ri!pagingInfo.startIndex <> 1,
        "STANDARD",
        "SECONDARY"
      ),
      size: ri!size
    ),
    " ",
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: {
        ri!pagingInfo.startIndex,
        " - ",
        if(
          ri!pagingInfo.startIndex + ri!pagingInfo.batchSize - 1 > ri!totalCount,
          ri!totalCount,
          ri!pagingInfo.startIndex + ri!pagingInfo.batchSize - 1
        )
      },
      size: ri!size,
      style: "STRONG"
    ),
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: { " of ", ri!totalCount },
      size: ri!size
    ),
    " ",
    #"SYSTEM_SYSRULES_richTextIcon"(
      icon: "angle-right",
      link: #"SYSTEM_SYSRULES_dynamicLink"(
        saveInto: {
          a!save(
            ri!pagingInfo.startIndex,
            ri!pagingInfo.startIndex + ri!pagingInfo.batchSize
          ),
          ri!additionalSaveInto,
          a!save(ri!pageNumber, ri!pageNumber + 1)
        },
        showWhen: ri!pagingInfo.startIndex + ri!pagingInfo.batchSize - 1 < ri!totalCount
      ),
      linkStyle: "STANDALONE",
      color: if(
        ri!pagingInfo.startIndex + ri!pagingInfo.batchSize - 1 < ri!totalCount,
        "STANDARD",
        "SECONDARY"
      ),
      size: ri!size
    ),
    #"SYSTEM_SYSRULES_richTextIcon"(
      icon: "angle-double-right",
      link: #"SYSTEM_SYSRULES_dynamicLink"(
        saveInto: {
          a!save(
            ri!pagingInfo.startIndex,
            if(
              mod(ri!totalCount, ri!pagingInfo.batchSize) = 0,
              ri!totalCount - ri!pagingInfo.batchSize + 1,
              ri!totalCount - mod(ri!totalCount, ri!pagingInfo.batchSize) + 1
            )
          ),
          ri!additionalSaveInto,
          a!save(ri!pageNumber, 1)
        },
        showWhen: ri!pagingInfo.startIndex + ri!pagingInfo.batchSize - 1 < ri!totalCount
      ),
      linkStyle: "STANDALONE",
      color: if(
        ri!pagingInfo.startIndex + ri!pagingInfo.batchSize - 1 < ri!totalCount,
        "STANDARD",
        "SECONDARY"
      ),
      size: ri!size
    ),
    ri!additionalRichTextItem
  },
  showWhen: and(
    #"_a-0000e3ff-a5bc-8000-9ba8-011c48011c48_39082"(ri!showWhen),
    ri!totalCount > 0,
    /*ux guidelines are to always show the pagingInfo if there are entries*/
    /*ri!totalCount > ri!pagingInfo.batchSize*/

  )
)
