interface.definition:
a!localVariables(
  local!questionnaireTemplate: ri!questionnaireTemplate,
  local!sortedCategories: #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_878178"(
    questionnaireTemplate: local!questionnaireTemplate
  ),
  /* Separating the ID and the item itself here allows us to maintain the selection after record action
        and also handle the deletion case */local!selectedCategoryId: a!refreshVariable(
    value: index(
      local!sortedCategories.questionCategoryTemplateId,
      1,
      null
    ),
    refreshOnReferencedVarChange: false
  ),
  local!selectedCategory: index(
    local!sortedCategories,
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73980"(
      nullableValue: wherecontains(
        local!selectedCategoryId,
        local!sortedCategories.questionCategoryTemplateId
      ),
      replacementValue: 1
    ),
    null
  ),
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            #"5d0ebcfe-19e8-47d5-a0ce-921f07e0ef1b"(
              questionnaireTemplate: local!questionnaireTemplate,
              manuallySelectedCategoryId: local!selectedCategoryId,
              selectedCategory: local!selectedCategory
            )
          },
          width: "NARROW_PLUS"
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: #"SYSTEM_SYSRULES_cardLayout"(
            padding: "LESS",
            showBorder: false,
            contents: {
              #"31ab10c1-04b6-4aa8-bdf8-fab1eac63cc8"(
                selectedCategory: local!selectedCategory
              ),
              #"0de536f2-9994-482d-bb05-337de9504351"(
                selectedCategory: local!selectedCategory,
                questionnaireTemplate: ri!questionnaireTemplate,
                i18nData: ri!i18nData,
                triggerRefresh: ri!triggerRefresh
              )
            }
          )
        )
      }
    )
  }
)
