rule.definition:
a!localVariables(
  local!populateDefaultThresholdRow: if(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!fieldData),
    false,
    or(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
        cdt: ri!fieldData,
        field: "fieldKey",
        value: #"_a-0000e528-bc07-8000-9bcf-011c48011c48_1037538"(isInternationalizedForAdminSetup: false).fieldKey
      ).required
    )
  ),
  local!populateDefaultFeeRow: if(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!fieldData),
    false,
    or(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
        cdt: ri!fieldData,
        field: "fieldKey",
        value: #"_a-0000e528-bc07-8000-9bcf-011c48011c48_1037524"(isInternationalizedForAdminSetup: false).fieldKey
      ).required
    )
  ),
  {
    a!save(
      ri!updatedProduct.thresholds,
      if(
        and(
          #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!updatedProduct.thresholds),
          local!populateDefaultThresholdRow
        ),
        'type!{urn:com:appian:types:AS:FS}AS_FS_PayThreshold'(productId: null),
        ri!updatedProduct.thresholds
      )
    ),
    a!save(
      ri!updatedProduct.fees,
      if(
        and(
          #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!updatedProduct.fees),
          local!populateDefaultFeeRow
        ),
        'type!{urn:com:appian:types:AS:FS}AS_FS_Fee'(productId: null),
        ri!updatedProduct.fees
      )
    )
  }
)
