interface.definition:
a!localVariables(
  local!numItemsPerColumn: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73980"(
    nullableValue: ri!numItemsPerColumn,
    replacementValue: 5
  ),
  local!columnsNeeded: tointeger(
    floor(
      count(ri!itemsToDisplay) / local!numItemsPerColumn
    )
  ) + if(
    tointeger(
      mod(
        count(ri!itemsToDisplay),
        local!numItemsPerColumn
      )
    ) = 0,
    0,
    1
  ),
  {
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      value: #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: ri!label,
        style: "STRONG",
        showWhen: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(ri!label)
      )
    ),
    if(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!itemsToDisplay),
      #"_a-0000e673-a4a7-8000-9bef-011c48011c48_1990725"(
        message: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
          i18nData: ri!i18nData,
          bundleKey: "AS.FS.AllBundles.lbl_None"
        )
      ),
      #"_a-0000e528-bc07-8000-9bcf-011c48011c48_1049475"(
        allFields: #"SYSTEM_SYSRULES_forEach"(
          items: enumerate(local!columnsNeeded) + 1,
          expression: {
            span: 1,
            components: #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: #"SYSTEM_SYSRULES_richTextBulletedList"(
                items: #"SYSTEM_SYSRULES_forEach"(
                  items: index(
                    ri!itemsToDisplay,
                    (
                      enumerate(
                        if(
                          fv!item = local!columnsNeeded,
                          if(
                            tointeger(
                              mod(
                                count(ri!itemsToDisplay),
                                local!numItemsPerColumn
                              )
                            ) = 0,
                            local!numItemsPerColumn,
                            tointeger(
                              mod(
                                count(ri!itemsToDisplay),
                                local!numItemsPerColumn
                              )
                            )
                          ),
                          local!numItemsPerColumn
                        )
                      ) + 1
                    ) + ((fv!item - 1) * local!numItemsPerColumn),
                    {}
                  ),
                  expression: if(
                    ri!internationalizeLabels,
                    #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(i18nData: ri!i18nData, bundleKey: fv!item),
                    fv!item
                  )
                )
              )
            )
          }
        )
      )
    )
  }
)
