rule.definition:
cast(
  typeof(ri!cdt),
  if(

    and(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(cleanwith(ri!field, ".[]")),
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(stripwith(ri!field, "1234567890")),
      not(#"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_162769"(
        value1: ri!cdt,
        value2: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_101176"()
      ))
    ),

    /*Early exit for a simple field-update*/
    #"_a-0000e6e2-62e5-8000-9bfe-011c48011c48_2375472"(
      cdt: ri!cdt,
      field: ri!field,
      indices: ri!indices,
      newValue: ri!newValue
    ),

    with(
      local!fieldsAndIndices: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75913"(field: ri!field),

      reduce(
        #"_a-0000e40a-de32-8000-9ba8-011c48011c48_268524"(
          previous: _,
          cdt: ri!cdt,
          fieldsAndIndices: local!fieldsAndIndices,
  /*Indices is only used when passing through from AS_CO_UT_updateCdtFields, never AS_CO_UT_updateCdtFieldsByPath*/
          indices: ri!indices,
          index: _
        ),
        ri!newValue,
        reverse(#"_a-0000e40a-de32-8000-9ba8-011c48011c48_268381"(input: local!fieldsAndIndices))
      )
    )
  )
)
