interface.definition:
a!localVariables(
  local!triggerTypeDefinitions: #"_a-0000e70f-2824-8000-9c01-011c48011c48_2555742"(i18nData: ri!i18nData),
  local!seletedTriggerTypeDefinition: index(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
      cdt: local!triggerTypeDefinitions,
      field: "triggerTypeCode",
      value: ri!triggerCondition.alertConfigurationTriggerTypeCode
    ),
    1,
    null(
      'type!{urn:com:appian:types:AS:FS}AS_FS_ALT_UNMAPPED_TriggerTypeConfiguration'
    )
  ),
  local!availableTriggerTypes: filter(
    #"_a-0000e70f-2824-8000-9c01-011c48011c48_2571222"(
      triggerTypeConfiguration: _,
      triggerSet: ri!alertConfigurationTriggerSet,
      triggerCondition: ri!triggerCondition
    ),
    local!triggerTypeDefinitions
  ),
  #"SYSTEM_SYSRULES_columnsLayout"(
    alignVertical: "MIDDLE",
    columns: {
      #"SYSTEM_SYSRULES_columnLayout"(
        width: "EXTRA_NARROW",
        contents: {
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            showWhen: ri!triggerConditionIndex <> 1,
            labelPosition: "COLLAPSED",
            value: ri!prefixLabel
          )
        }
      ),
      /*Trigger Dropdown*/
      #"SYSTEM_SYSRULES_columnLayout"(
        width: "NARROW",
        contents: #"_a-0000e528-bc07-8000-9bcf-011c48011c48_1027802"(
          label: "Trigger type dropdown",
          labelPosition: "COLLAPSED",
          choiceLabels: local!availableTriggerTypes.triggerTypeLabel,
          choiceValues: local!availableTriggerTypes.triggerTypeCode,
          value: ri!triggerCondition.alertConfigurationTriggerTypeCode,
          saveInto: {
            ri!triggerCondition.alertConfigurationTriggerTypeCode,
            #"_a-0000e3c8-6861-8000-9ba2-011c48011c48_124304"(
              ri!triggerCondition.configurationTriggerSettings
            )
          },
          required: true(),
          placeholderLabel: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
            i18nData: ri!i18nData,
            bundleKey: "AS.FS.AllBundles.plc_Field"
          )
        )
      ),
      /*Additional components*/
      #"SYSTEM_SYSRULES_columnLayout"(
        showWhen: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(
          local!seletedTriggerTypeDefinition.additionalTriggerConfigurationComponents
        ),
        contents: local!seletedTriggerTypeDefinition.additionalTriggerConfigurationComponents(
          /*Not all additional components will need this input, but it is included for flexibility*/
          alertConfigurationTriggerSet: ri!alertConfigurationTriggerSet,
          readOnly: false(),
          i18nData: ri!i18nData,
          triggerCondition: ri!triggerCondition
        )
      ),
      /*Remove Trigger*/
      #"SYSTEM_SYSRULES_columnLayout"(
        /*only display when more than one trigger exists*/
        showWhen: #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_880586"(
          value: ri!alertConfigurationTriggerSet.triggerConditions
        ) > 1,
        contents: #"SYSTEM_SYSRULES_richTextDisplayField"(
          label: "Remove Trigger",
          labelPosition: "COLLAPSED",
          value: {
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: "times-circle-o",
              color: "SECONDARY",
              size: "MEDIUM",
              linkStyle: "STANDALONE",
              altText: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                i18nData: ri!i18nData,
                bundleKey: "AS.FS.AllBundles.lbl_RemoveTriggerConditionX",
                arguments: ri!triggerConditionIndex
              ),
              link: #"SYSTEM_SYSRULES_dynamicLink"(
                saveInto: {
                  a!save(
                    ri!alertConfigurationTriggerSet.triggerConditions,
                    remove(
                      ri!alertConfigurationTriggerSet.triggerConditions,
                      ri!triggerConditionIndex
                    )
                  )
                }
              )
            )
          }
        ),
        width: "EXTRA_NARROW"
      )
    }
  )
)
