rule.definition:
a!localVariables(
  local!action: #"_a-0000e70f-2824-8000-9c01-011c48011c48_2539235"(
    json: ri!configurationTriggerSettings,
    field: "action",
    indexDefault: null
  ),
  local!activeOriginalEntityRelationships: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
    cdt: ri!auditParameters.originalObjects,
    field: "isActive",
    value: true
  ),
  local!activeNewEntityRelationships: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
    cdt: ri!auditParameters.newObjects,
    field: "isActive",
    value: true
  ),
  if(
    local!action = #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_181596-adt",
    /*check for specified modification - tailored to numeric thresholds*/
    a!localVariables(
      local!field: #"_a-0000e70f-2824-8000-9c01-011c48011c48_2539235"(
        json: ri!configurationTriggerSettings,
        field: "modificationTriggerCriteria.field",
        indexDefault: null
      ),
      local!operator: #"_a-0000e70f-2824-8000-9c01-011c48011c48_2539235"(
        json: ri!configurationTriggerSettings,
        field: "modificationTriggerCriteria.operator",
        indexDefault: null
      ),
      local!threshold: #"_a-0000e70f-2824-8000-9c01-011c48011c48_2539235"(
        json: ri!configurationTriggerSettings,
        field: "modificationTriggerCriteria.threshold",
        indexDefault: null
      ),
      local!originalEntityRelationsFulfillingTriggerCondition: #"_a-0000e70f-2824-8000-9c01-011c48011c48_2571943"(
        cdt: local!activeOriginalEntityRelationships,
        field: local!field,
        operator: local!operator,
        value: local!threshold
      ),
      local!newEntityRelationsFulfillingTriggerCondition: #"_a-0000e70f-2824-8000-9c01-011c48011c48_2571943"(
        cdt: local!activeNewEntityRelationships,
        field: local!field,
        operator: local!operator,
        value: local!threshold
      ),
      /*if entity relation A is over the threshold, and an action is taken which moves A below the threshold and*/
      /*B above the threshold, a new alert will be generated*/
      if(
        ri!isOriginalObjectEvaluation,
        /*Original object evaluation is true if any old percents are over the threshold and there are no new entities that were updated to be over the threshold*/
        and(
          #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(
            local!originalEntityRelationsFulfillingTriggerCondition
          ),
          #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(
            difference(
              tointeger(
                index(
                  local!newEntityRelationsFulfillingTriggerCondition,
                  "customerRelationId",
                  null
                )
              ),
              tointeger(
                index(
                  local!originalEntityRelationsFulfillingTriggerCondition,
                  "customerRelationId",
                  null
                )
              )
            )
          )
        ),
        /*Updated object evaluation is true if any new percents are over the threshold*/
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(
          local!newEntityRelationsFulfillingTriggerCondition
        )
      )
    ),
    local!action = #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_181584-adt",
    if(
      ri!isOriginalObjectEvaluation,
      /*cannot evaluate transitory triggers on the original object*/
      false,
      /*check for mismatch in pks between original and new object*/
      a!localVariables(
        local!originalPrimaryKeys: tointeger(
          index(
            local!activeOriginalEntityRelationships,
            "customerRelationId",
            {}
          )
        ),
        local!newPrimaryKeys: tointeger(
          index(
            local!activeNewEntityRelationships,
            "customerRelationId",
            {}
          )
        ),
        /*if any new pks don't exist in the list of old pks, return true*/
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(
          difference(
            local!newPrimaryKeys,
            local!originalPrimaryKeys
          )
        )
      )
    ),
    /*we deactivate entity relationships instead of deleting*/
    local!action = #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_181602-adt",
    if(
      ri!isOriginalObjectEvaluation,
      /*cannot evaluate transitory triggers on the original object*/
      false,
      /*check for mismatch in pks between original and new object*/
      /*we could just check for the existance of any deactivated entries, but a comparison of active entries is safer*/
      a!localVariables(
        local!originalPrimaryKeys: tointeger(
          index(
            local!activeOriginalEntityRelationships,
            "customerRelationId",
            {}
          )
        ),
        local!newPrimaryKeys: tointeger(
          index(
            local!activeNewEntityRelationships,
            "customerRelationId",
            {}
          )
        ),
        /*if any old pks don't exist in the list of new pks, return true*/
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(
          difference(
            local!originalPrimaryKeys,
            local!newPrimaryKeys
          )
        )
      )
    ),
    error(
      "Invalid entity relation action code in triggerConditionId: " & ri!triggerConditionId
    )
  )
)

