rule.definition:
#"SYSTEM_SYSRULES_forEach"(
  items: ri!alertConfigurations,
  expression: a!localVariables(
    /*filter out event type codes that don't exist in the env*/
    local!generatedEventCodes: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
      cdt: fv!item.events,
      field: "eventTypeCode",
      value: index(
        #"_a-0000e70f-2824-8000-9c01-011c48011c48_2541492"(),
        "eventTypeCode",
        {}
      )
    ).eventTypeCode,
    local!message: if(
      count(local!generatedEventCodes) = 0,
      fv!item.alertText,
      "AS.FS.AllBundles.lbl_AlertMessageWithEventCreation"
    ),
    local!arguments: if(
      count(local!generatedEventCodes) = 0,
      {},
      append(
        'type!{urn:com:appian:types:AS:ALT}AS_ALT_AlertArgument'(
          argument: fv!item.alertText,
          argumentDisplayType: null
        ),
        'type!{urn:com:appian:types:AS:ALT}AS_ALT_AlertArgument'(
          argument: if(
            count(local!generatedEventCodes) = 1,
            "AS.FS.AllBundles.lbl_AlertSingleEventCreation",
            "AS.FS.AllBundles.lbl_AlertMultipleEventCreation"
          ),
          argumentDisplayType: #"_a-0000e70f-2824-8000-9c01-011c48011c48_2608118",
          nestedArguments: #"SYSTEM_SYSRULES_forEach"(
            local!generatedEventCodes,
            'type!{urn:com:appian:types:AS:ALT}AS_ALT_AlertArgument'(
              /* This will be updated to the ID after creation of the event object */
              argument: null,
              argumentDisplayType: fv!item
            )
          )
        )
      )
    ),
    #"_a-0000e50d-bf1d-8000-9bcd-011c48011c48_994447"(
      alertSetId: ri!alertSetId,
      alertConfigurationId: fv!item.alertConfigurationId,
      message: local!message,
      arguments: local!arguments,
      createdBy: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73366"
    )
  )
)
