interface.definition:
a!localVariables(
  /* Stores the group type selected from the dropdown*/
  local!parentGroup: if(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!group),
    null,
    togroup(group(ri!group, "parentId"))
  ),
  /* When true, the Add User search box is shown*/
  local!showAddMember: a!refreshVariable(
    value: false,
    /* Clear on changed group selection */
    refreshOnVarChange: ri!group
  ),
  /* Returns the groups that are part of the selected parent group(group that is selected from the dropdown)*/
  local!subgroups: a!refreshVariable(
    value: if(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!group),
      {},
      #"SYSTEM_SYSRULES_forEach"(
        items: a!groupMembers(
          pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
            startIndex: 1,
            /*current max batchSize for this function*/
            batchSize: #"_a-0000e447-933d-8000-9baa-011c48011c48_43371"
          ),
          group: togroup(ri!group)
        ).data,
        expression: if(
          runtimetypeof(fv!item) = #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_166426-io",
          fv!item,
          {}
        )
      )
    ),
    refreshAlways: true
  ),
  /* Returns the direct members of the selected parent group */
  local!directUsers: a!refreshVariable(
    value: if(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!group),
      {},
      #"SYSTEM_SYSRULES_forEach"(
        items: a!groupMembers(
          pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
            startIndex: 1,
            /*current max batchSize for this function*/
            batchSize: #"_a-0000e447-933d-8000-9baa-011c48011c48_43371"
          ),
          group: ri!group,
          direct: true,
          memberType: "USER"
        ).data,
        expression: 'type!{urn:com:appian:types:AS:GM}AS_GM_P_UserInformation'(
          name: fv!item,
          isDirect: true,
          isGroup: if(
            runtimetypeof(fv!item) = #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_166426-io",
            true,
            false
          ),
          groupId: if(
            runtimetypeof(fv!item) = #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_166426-io",
            group(fv!item, "id"),
            {}
          )
        )
      )
    ),
    refreshAlways: true
  ),
  /* Returns all the members of the selected parent group(both direct and indirect) */
  local!indirectUsers: a!refreshVariable(
    value: if(
      or(
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!group),
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(local!parentGroup)
      ),
      #"_a-0000e3d1-f4dc-8000-9ba4-011c48011c48_143475"(
        type: 'type!{urn:com:appian:types:AS:GM}AS_GM_P_UserInformation'
      ),
      #"SYSTEM_SYSRULES_forEach"(
        items: a!groupMembers(
          pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
            startIndex: 1,
            /*current max batchSize for this function*/
            batchSize: #"_a-0000e447-933d-8000-9baa-011c48011c48_43371"
          ),
          group: togroup(ri!group),
          memberType: "USER"
        ).data,
        expression: 'type!{urn:com:appian:types:AS:GM}AS_GM_P_UserInformation'(
          name: fv!item,
          isDirect: if(
            runtimetypeof(fv!item) = #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_166426-io",
            contains(
              togroup(index(local!directUsers, "name", {})),
              fv!item
            ),
            contains(
              touser(index(local!directUsers, "name", {})),
              fv!item
            )
          ),
          isGroup: runtimetypeof(fv!item) = #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_166426-io",
          groupId: if(
            runtimetypeof(fv!item) = #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_166426-io",
            group(fv!item, "id"),
            {}
          ),
          listOfParentGroups: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_167567-io"(groupList: local!subgroups, user: fv!item)
        )
      )
    ),
    refreshAlways: true
  ),
  /*Stores list of users that need to be added to the group */
  local!addUser: a!refreshVariable(
    value: null(#"_a-0000e40a-de32-8000-9ba8-011c48011c48_283300"()),
    refreshOnVarChange: ri!group
  ),
  /*Stores list of users that need to be removed from the group */
  local!userToRemove: a!refreshVariable(
    value: null('type!{urn:com:appian:types:AS:GM}AS_GM_P_UserInformation?list'),
    refreshOnVarChange: ri!group
  ),
  /*Stores the current grid selection */
  local!selectedRows: a!refreshVariable(
    value: null('type!{urn:com:appian:types:AS:GM}AS_GM_P_UserInformation?list'),
    refreshOnVarChange: ri!group
  ),
  local!userDetails: a!refreshVariable(
    value: null('type!{urn:com:appian:types:AS:GM}AS_GM_P_UserInformation?list'),
    refreshOnVarChange: ri!group
  ),
  local!createNewMember: a!refreshVariable(
    value: false,
    refreshOnVarChange: ri!group
  ),
  {
    /*Header*/
    #"SYSTEM_SYSRULES_sideBySideLayout"(
      alignVertical: "MIDDLE",
      marginBelow: "NONE",
      items: {
        /*group name*/
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"SYSTEM_SYSRULES_richTextDisplayField"(
            labelPosition: "COLLAPSED",
            value: {
              #"SYSTEM_SYSRULES_richTextHeader"(
                text: group(ri!group, "groupName"),
                size: "LARGE"
              )
            }
          )
        ),
        /*actions*/
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"SYSTEM_SYSRULES_ButtonLayout"(
            primaryButtons: {
              #"SYSTEM_SYSRULES_ButtonWidget"(
                label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                  i18nData: ri!i18nData,
                  bundleKey: "AS.GM.GroupManagement.lbl_AddMembers"
                ),
                value: true,
                validate: true,
                saveInto: { local!showAddMember },
                size: "SMALL",
                style: "SECONDARY",
                disabled: or(
                  local!showAddMember = true,
                  #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!group)
                )
              ),
              #"SYSTEM_SYSRULES_ButtonWidget"(
                label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                  i18nData: ri!i18nData,
                  bundleKey: "AS.GM.GroupManagement.lbl_RemoveMembers"
                ),
                confirmHeader: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                  i18nData: ri!i18nData,
                  bundleKey: "AS.GM.GroupManagement.lbl_RemoveMembers"
                ),
                confirmMessage: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                  i18nData: ri!i18nData,
                  bundleKey: "AS.GM.GroupManagement.lbl_ModifyAccessRights"
                ),
                confirmButtonLabel: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                  i18nData: ri!i18nData,
                  bundleKey: "AS.GM.GroupManagement.lbl_YesRemove"
                ),
                cancelButtonLabel: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                  i18nData: ri!i18nData,
                  bundleKey: "AS.GM.GroupManagement.lbl_NoGoback"
                ),
                saveInto: {
                  if(
                    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(
                      index(
                        local!selectedRows,
                        wherecontains(
                          true,
                          toboolean(
                            index(local!selectedRows, "isDirect", {})
                          )
                        ),
                        {}
                      )
                    ),
                    {},
                    {
                      #"SYSTEM_SYSRULES_startProcess"(
                        processModel: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_160280-io",
                        processParameters: {
                          userToRemove: #"SYSTEM_SYSRULES_forEach"(
                            items: index(
                              local!selectedRows,
                              wherecontains(
                                true,
                                toboolean(
                                  index(local!selectedRows, "isDirect", {})
                                )
                              ),
                              {}
                            ),
                            expression: if(
                              index(fv!item, "isGroup", {}) = true,
                              togroup(index(fv!item, "groupId", {})),
                              touser(index(fv!item, "name", {}))
                            )
                          ),
                          selectedGroup: ri!group,
                          initiator: loggedInUser()
                        }
                      )
                    }
                  ),
                  a!save(local!userToRemove, null),
                  a!save(
                    local!selectedRows,
                    #"_a-0000e3d1-f4dc-8000-9ba4-011c48011c48_143475"(
                      type: 'type!{urn:com:appian:types:AS:GM}AS_GM_P_UserInformation?list'
                    )
                  )
                },
                size: "SMALL",
                style: "SECONDARY",
                disabled: or(
                  #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(local!userToRemove),
                  contains(
                    toboolean(
                      index(local!selectedRows, "isDirect", null)
                    ),
                    false
                  )
                )
              )
            }
          ),
          width: "MINIMIZE"
        )
      }
    ),
    /*  Add users section */
    #"f55ef55b-854d-44f5-bdc6-cec31bce739c"(
      parentGroupsToValidate: ri!parentGroupsToValidate,
      createNewMember: local!createNewMember,
      i18nData: ri!i18nData,
      userDetails: local!userDetails,
      addUser: local!addUser,
      parentGroup: local!parentGroup,
      memberType: "USER",
      showAddMember: local!showAddMember,
      groupSelected: ri!group,
      indirectUsers: local!indirectUsers
    ),
    /*  Add/Remove member buttons    */
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      value: {
        #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "info-circle",
          color: "ACCENT",
          size: "MEDIUM"
        ),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: " " & #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
            i18nData: ri!i18nData,
            bundleKey: "AS.GM.GroupManagement.lbl_SelectDirectMembersRemove"
          )
        )
      },
      showWhen: contains(
        toboolean(
          index(local!selectedRows, "isDirect", null)
        ),
        false
      )
    ),
    /*  Grid that displays list of users/groups*/
    #"SYSTEM_SYSRULES_cardLayout"(
      padding: "NONE",
      showBorder: false,
      contents: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_173935-io"(
        indirectUsers: local!indirectUsers,
        selectedRows: local!selectedRows,
        userToRemove: local!userToRemove,
        groupName: ri!group,
        parentGroup: local!parentGroup,
        memberType: "USER",
        i18nData: ri!i18nData
      )
    )
  }
)
