interface.definition:
a!localVariables(
  local!alertSourceInitiatorValues: a!flatten(ri!alertTypes.initiatorValues),
  #"SYSTEM_SYSRULES_sideBySideLayout"(
    alignVertical: "MIDDLE",
    items: {
      #"SYSTEM_SYSRULES_sideBySideItem"(
        item: #"SYSTEM_SYSRULES_richTextDisplayField"(
          labelPosition: "COLLAPSED",
          value: {
            #"SYSTEM_SYSRULES_richTextHeader"(
              text: {
                #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                  i18nData: ri!i18nData,
                  bundleKey: "AS.ALT.Alerts.lbl_ActiveAlerts"
                )
              },
              size: "MEDIUM"
            )
          }
        ),
        width: "AUTO"
      ),
      /*Type*/
      #"SYSTEM_SYSRULES_sideBySideItem"(
        item: #"_a-0000e528-bc07-8000-9bcf-011c48011c48_1032072"(
          disabled: and(
            #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(ri!selectedSources),
            #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!selectedTypes)
          ),
          label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
            i18nData: ri!i18nData,
            bundleKey: "AS.FS.AllBundles.lbl_AlertType"
          ),
          labelPosition: "COLLAPSED",
          placeholderOverride: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
            i18nData: ri!i18nData,
            bundleKey: "AS.FS.AllBundles.lbl_AnyType"
          ),
          wrapInMarkers: false(),
          choiceLabels: ri!alertTypes.label,
          choiceValues: ri!alertTypes.value,
          saveInto: {
            ri!selectedTypes,
            if(
              #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!selectedTypes),
              /*if no type is selected, display all source options*/
              a!save(
                local!alertSourceInitiatorValues,
                a!flatten(ri!alertTypes.initiatorValues)
              ),
              /*otherwise filter source options to the selected type(s)*/
              a!save(
                local!alertSourceInitiatorValues,
                a!flatten(
                  #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
                    cdt: ri!alertTypes,
                    field: "value",
                    value: ri!selectedTypes
                  ).initiatorValues
                )
              )
            ),
            a!save(ri!selectedSources, null)
          },
          value: ri!selectedTypes
        ),
        width: "AUTO"
      ),
      /*Source*/
      #"SYSTEM_SYSRULES_sideBySideItem"(
        item: #"_a-0000e528-bc07-8000-9bcf-011c48011c48_1032072"(
          label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
            i18nData: ri!i18nData,
            bundleKey: "AS.FS.AllBundles.lbl_AlertSource"
          ),
          labelPosition: "COLLAPSED",
          placeholderOverride: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
            i18nData: ri!i18nData,
            bundleKey: "AS.FS.AllBundles.lbl_AnySource"
          ),
          wrapInMarkers: false(),
          choiceLabels: #"SYSTEM_SYSRULES_forEach"(
            items: local!alertSourceInitiatorValues,
            expression: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73660"(user: fv!item, i18nData: ri!i18nData)
          ),
          choiceValues: local!alertSourceInitiatorValues,
          saveInto: ri!selectedSources,
          value: ri!selectedSources
        ),
        width: "AUTO"
      )
    }
  )
)
