rule.definition:
/*return a list of maps with tasks*/
a!localVariables(
  local!dependentTasks: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75720"(
    cdt: a!flatten(
      if(
        #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!precedentTask),
        /*if there is no precedent, get all tasks with no precedent*/
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!allTasks,
          expression: if(
            #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(fv!item.taskPrecedents),
            fv!item,
            {}
          )
        ),
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!allTasks,
          expression: if(
            or(
              /*this shouldn't be triggered but will prevent infinite loop scenario*/
              #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(fv!item.taskPrecedents),
              contains(
                #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76433"(
                  cdt: ri!precedentTask,
                  field: "taskPrecedents.taskId_precedent",
                  indexDefault: tointeger({})
                ),
                #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76433"(
                  cdt: fv!item,
                  field: "taskPrecedents.taskId_precedent",
                  indexDefault: tointeger({})
                )
              )
            ),
            {},
            /*check if each task's precedent is the precedentTask*/
            if(
              contains(
                fv!item.taskPrecedents.taskId_precedent,
                if(
                  #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!precedentTask.taskId),
                  /*To get task dependency on process setup */
                  wherecontains(ri!precedentTask, ri!allTasks),
                  ri!precedentTask.taskId
                )
              ),
              fv!item,
              {}
            )
          )
        )
      )
    ),
    field: "dueDate",
    ascending: true
  ),
  local!dependents: #"SYSTEM_SYSRULES_forEach"(
    items: local!dependentTasks,
    expression: #"_a-0000e776-697a-8000-9c05-011c48011c48_2827461"(
      precedentTask: fv!item,
      alltasks: ri!allTasks,
      level: ri!level + 1
    )
  ),
  a!map(
    task: ri!precedentTask,
    dependents: local!dependents,
    numTotalDependents: sum(
      tointeger(
        /*for each task, add 1 plus num of total dependents*/
        #"SYSTEM_SYSRULES_forEach"(
          items: local!dependents,
          expression: 1 + index(fv!item, "numTotalDependents", 0)
        )
      )
    )
  )
)
