interface.definition:
a!localVariables(
  local!showGrid: false(),
  local!triggerRefresh: a!refreshVariable(
    value: now(),
    refreshAfter: "RECORD_ACTION"
  ),
  local!dateFilter: #"_a-0000e539-6d59-8000-9bd0-011c48011c48_1209521"(i18nData: ri!i18nData)[1].endDate,
  local!refData: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_39864"(
    typeList: { #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_40302" },
    i18nData: ri!i18nData
  ),
  local!aggregatedRequests: #"_a-0000e6e2-62e5-8000-9bfe-011c48011c48_2501604"(
    returnType: #"_a-0000e415-3035-8000-9bb2-011c48011c48_290169",
    triggerRefresh: local!triggerRefresh,
    aggregation: #"SYSTEM_SYSRULES_aggregationFields"(
      groupings: {
        #"SYSTEM_SYSRULES_grouping"(
          field: #"urn:appian:record-field:v1:04c66223-4089-4d5e-8ed8-6a853822f478/069d4b4c-cfef-4e03-bea4-aa577ef40a73",
          alias: "type"
        ),
        #"SYSTEM_SYSRULES_grouping"(
          field: #"urn:appian:record-field:v1:04c66223-4089-4d5e-8ed8-6a853822f478/c0423faa-e9c8-49a9-a393-1b1562782280",
          alias: "status"
        )
      },
      measures: #"SYSTEM_SYSRULES_measure"(
        field: #"urn:appian:record-field:v1:04c66223-4089-4d5e-8ed8-6a853822f478/9e449e60-b0c4-497f-9284-80ec663f2e7e",
        function: "COUNT",
        alias: "count"
      )
    ),
    statusCodes: #"_a-0000e539-6d59-8000-9bd0-011c48011c48_1209491"(),
    logicalExpression: {
      #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "OR",
        logicalexpressions: {
          /*has not been funded and estimated funding date is in range*/
          #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: #"urn:appian:record-field:v1:04c66223-4089-4d5e-8ed8-6a853822f478/26081066-e5eb-44de-a15e-ecf1aaf6f1b0/f67e6d9a-f79b-458e-8d56-b5c70bb6cde5",
                operator: "between",
                value: { today(), local!dateFilter }
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: #"urn:appian:record-field:v1:04c66223-4089-4d5e-8ed8-6a853822f478/26081066-e5eb-44de-a15e-ecf1aaf6f1b0/4290ce1a-74eb-4129-b2f4-c16272f56076",
                operator: "is null"
              )
            }
          ),
          /*has been funded in range (today) - definitely an edge case*/
          #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: #"urn:appian:record-field:v1:04c66223-4089-4d5e-8ed8-6a853822f478/26081066-e5eb-44de-a15e-ecf1aaf6f1b0/4290ce1a-74eb-4129-b2f4-c16272f56076",
                operator: "not null"
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: #"urn:appian:record-field:v1:04c66223-4089-4d5e-8ed8-6a853822f478/26081066-e5eb-44de-a15e-ecf1aaf6f1b0/4290ce1a-74eb-4129-b2f4-c16272f56076",
                operator: "between",
                value: { today(), local!dateFilter }
              )
            }
          )
        }
      )
    }
  ),
  local!requestsByType: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75720"(
    cdt: #"SYSTEM_SYSRULES_forEach"(
      items: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73758"(value: local!aggregatedRequests.type),
      expression: {
        type: fv!item,
        totalCount: sum(
          #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
            cdt: local!aggregatedRequests,
            field: "type",
            value: fv!item
          ).count
        )
      }
    ),
    field: "totalCount",
    ascending: false
  ),
  local!topNIndex: min(
    5,
    #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_880586"(local!requestsByType)
  ),
  local!topRequestTypes: index(
    local!requestsByType,
    enumerate(local!topNIndex) + 1,
    {}
  ),
  local!statuses: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73758"(
    index(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
        cdt: local!aggregatedRequests,
        field: "type",
        value: index(local!topRequestTypes, "type", {})
      ),
      "status",
      {}
    )
  ),
  local!seriesData: #"SYSTEM_SYSRULES_forEach"(
    items: local!statuses,
    expression: a!localVariables(
      local!status: fv!item,
      local!seriesData: #"SYSTEM_SYSRULES_forEach"(
        items: local!topRequestTypes.type,
        expression: a!defaultValue(
          value: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81482"(
            cdt: local!aggregatedRequests,
            fields: { "type", "status" },
            values: { fv!item, local!status }
          ).count,
          /*replace null values with 0*/
          default: 0
        )
      ),
      #"SYSTEM_SYSRULES_chartSeries"(
        label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
          i18nData: ri!i18nData,
          bundleKey: #"_a-0000e586-af6f-8000-9bd2-011c48011c48_1320404"(statusCode: local!status)
        ),
        data: local!seriesData
      )
    )
  ),
  local!categories: #"SYSTEM_SYSRULES_forEach"(
    items: index(local!topRequestTypes, "type", {}),
    expression: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_32654"(refData: local!refData, code: fv!item)
  ),
  {
  #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3312448"(
    "AS_IO_CPS_onboardingTypes"
  ),
  #"_a-0000e77d-004e-8000-9c08-011c48011c48_2838871"(
    header: #"SYSTEM_SYSRULES_sideBySideLayout"(
      items: {
        /*Label*/
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"SYSTEM_SYSRULES_richTextDisplayField"(
            labelPosition: "COLLAPSED",
            value: {
              #"SYSTEM_SYSRULES_richTextHeader"(
                text: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                  i18nData: ri!i18nData,
                  bundleKey: "AS.CO.AllBundles.lbl_TopTypes"
                ),
                size: "MEDIUM"
              )
            }
          )
        ),
        /*Date Filter*/
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"_a-0000e77d-004e-8000-9c08-011c48011c48_2834839"(
            label: "OnboardingType",
            i18nData: ri!i18nData,
            date: local!dateFilter
          ),
          width: "MINIMIZE"
        ),
        /*Toggle report View*/
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"_a-0000e77d-004e-8000-9c08-011c48011c48_2835419"(
            i18nData: ri!i18nData,
            showGrid: local!showGrid
          ),
          width: "MINIMIZE"
        )
      },
      alignVertical: "MIDDLE"
    ),
    contents: {
      #"_a-0000e776-697a-8000-9c05-011c48011c48_2826556"(
        categories: local!categories,
        chartSeries: local!seriesData,
        columnHeading: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
          i18nData: ri!i18nData,
          bundleKey: "AS.CO.AllBundles.lbl_OnboardingType"
        ),
        showWhen: local!showGrid
      ),
      #"_a-0000e777-b9f8-8000-9c06-011c48011c48_2828210"(
        label: "AS.CO.AllBundles.lbl_TopOnboardingTypes",
        i18nData: ri!i18nData,
        categories: local!categories,
        chartSeries: local!seriesData,
        showWhen: not(local!showGrid)
      )
    }
  )
  }
)
