interface.definition:
a!localVariables(
  local!showGrid: false(),
  local!triggerRefresh: a!refreshVariable(
    value: now(),
    refreshAfter: "RECORD_ACTION"
  ),
  local!dateFilter: #"_a-0000e539-6d59-8000-9bd0-011c48011c48_1209521"(i18nData: ri!i18nData)[1].endDate,
  local!typeRefData: #"_a-0000e774-a792-8000-9c04-011c48011c48_2822005"(
    returnType: #"_a-0000e415-3035-8000-9bb2-011c48011c48_290163",
    i18nData: ri!i18nData,

  ),
  local!statusRefData: #"_a-0000e539-6d59-8000-9bd0-011c48011c48_1207675"(),
  local!aggregatedTasks: #"_a-0000e77e-5277-8000-9c09-011c48011c48_2842625"(
    returnType: #"_a-0000e415-3035-8000-9bb2-011c48011c48_290169",
    triggerRefresh: local!triggerRefresh,
    aggregationFields: #"SYSTEM_SYSRULES_aggregationFields"(
      groupings: {
        #"SYSTEM_SYSRULES_grouping"(
          field: #"urn:appian:record-field:v1:31b77cbe-8b51-41ba-927f-f11ee2d7fd33/e5531196-36b9-4763-8c0b-023acf6fe853",
          alias: "type"
        ),
        #"SYSTEM_SYSRULES_grouping"(
          field: #"urn:appian:record-field:v1:31b77cbe-8b51-41ba-927f-f11ee2d7fd33/41d6e4c9-61dc-49d1-b5e0-df8ff406b6b4",
          alias: "status"
        )
      },
      measures: #"SYSTEM_SYSRULES_measure"(
        field: #"urn:appian:record-field:v1:31b77cbe-8b51-41ba-927f-f11ee2d7fd33/9628d90f-e047-41dd-a8f7-8c90ef76e2ca",
        function: "COUNT",
        alias: "count"
      )
    ),
    taskStatusIds: {
      #"_a-0000e460-9d93-8000-9bc1-011c48011c48_603236",
      #"_a-0000e460-9d93-8000-9bc1-011c48011c48_603230",
      #"_a-0000e460-9d93-8000-9bc1-011c48011c48_603242",
      #"_a-0000e673-a4a7-8000-9bef-011c48011c48_1952244"
    },
    additionalLogicalExpressions: #"SYSTEM_SYSRULES_queryLogicalExpression"(
      operator: "AND",
      filters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: #"urn:appian:record-field:v1:31b77cbe-8b51-41ba-927f-f11ee2d7fd33/58a8f13e-7f46-49da-b15a-beed1f794261",
          operator: ">=",
          value: today()
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: #"urn:appian:record-field:v1:31b77cbe-8b51-41ba-927f-f11ee2d7fd33/58a8f13e-7f46-49da-b15a-beed1f794261",
          operator: "<",
          value: local!dateFilter
        )
      }
    ),

  ),
  local!aggregatedTasksWithLabel: #"SYSTEM_SYSRULES_forEach"(
    items: local!aggregatedTasks,
    expression: {
      type: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
        i18nData: ri!i18nData,
        bundleKey: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
          cdt: local!typeRefData,
          field: "taskBehaviorTypeId",
          value: fv!item.type
        ).behaviorDisplayName
      ),
      status: fv!item.status,
      count: fv!item.count
    }
  ),
  local!tasksByType: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75720"(
    cdt: #"SYSTEM_SYSRULES_forEach"(
      items: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73758"(
        value: local!aggregatedTasksWithLabel.type
      ),
      expression: {
        type: fv!item,
        totalCount: sum(
          #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
            cdt: local!aggregatedTasksWithLabel,
            field: "type",
            value: fv!item
          ).count
        )
      }
    ),
    field: "totalCount",
    ascending: false
  ),
  local!topNIndex: min(
