    #"_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_880586"(local!tasksByType)
  ),
  local!topTaskTypes: todatasubset(
    arrayToPage: local!tasksByType,
    pagingConfiguration: #"SYSTEM_SYSRULES_pagingInfo"(
