rule.definition:
a!localVariables(
  local!username: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73980"(
    nullableValue: ri!username,
    replacementValue: loggedInUser()
  ),
  local!isUserManager:     #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_236057"(username: local!username),
  #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_187421"(
    value: ri!assigneeTypeFilter,
    inArray: #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_217594"().value,
    replacement: {
      /* All -- only filter if we are applying some type of group filter */
      if(
        ri!applyGroupFilter,
        #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "OR",
          logicalExpressions: {
            #"SYSTEM_SYSRULES_queryLogicalExpression"(
              operator: "AND",
              filters: {
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: ri!groupAssigneeField,
                  operator: "in",
                  value: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73980"(
                    nullableValue: ri!groupAssignee,
                    replacementValue: #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_249947"(username: local!username)
                  )
                ),
                /*Only restrict to unassigned if they are not a manager*/
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: ri!userAssigneeField,
                  operator: "is null",
                  applyWhen: and(
                    not(local!isUserManager),
                    #"_a-0000e3ff-a5bc-8000-9ba8-011c48011c48_39082"(ri!applyManagerFilter)
                ))
              }
            ),
            #"SYSTEM_SYSRULES_queryLogicalExpression"(
              operator: "AND",
              filters: #"SYSTEM_SYSRULES_queryFilter"(
                field: ri!userAssigneeField,
                operator: "=",
                value: local!username
              )
            )
          }
        ),
        null
      ),
      /* Assigned to me */
      #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: ri!userAssigneeField,
            operator: "=",
            value: local!username
          )
        }
      ),
      /* Unassigned */
      #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: ri!groupAssigneeField,
            operator: "in",
            value: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73980"(
              nullableValue: ri!groupAssignee,
              replacementValue: #"_a-0000e3e6-fb42-8000-9ba5-011c48011c48_249947"(username: local!username)
            ),
            applyWhen: ri!applyGroupFilter
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: ri!userAssigneeField,
            operator: "is null"
          )
        }
      )
    },
    customDefault: {}
  )
