interface.definition:
a!localVariables(
  /* We default to a range = the difference between today and the datefilter.  Could easily be updated to be dynamic. */
  local!movingAverageRange: tointeger(today() - ri!dateFilter),
  local!fullDisplayTimeFilter: ri!dateFilter - local!movingAverageRange,
  local!onboardingRequestsCompletedDuringFullMovingAverageTimePeriod: #"_a-0000e471-623a-8000-9bc3-011c48011c48_785571"(
    returnType: #"_a-0000e415-3035-8000-9bb2-011c48011c48_290163",
    type: ri!onboardingTypesFilter,
    /* Onboarding cannot be modified after being completed, so that serves as completion date */
    modifiedDate: local!fullDisplayTimeFilter,
    modifiedDateFilterOperator: ">="
  ),
  local!onboardingRequestsCompletionDaysMovingAverageData: #"SYSTEM_SYSRULES_forEach"(
    items: ri!dateFilter + enumerate(local!movingAverageRange),
    expression: a!localVariables(
      local!onboardingRequestsCompleteInMovingAverageRangeForDate: #"_a-0000e607-97fd-8000-9be9-011c48011c48_1654733"(
        cdt: local!onboardingRequestsCompletedDuringFullMovingAverageTimePeriod,
        startDate: fv!item - local!movingAverageRange,
        endDate: fv!item,
        dateField: "modifiedDate"
      ),
      a!map(
        dateStamp: fv!item,
        avgDays: fixed(
          #"_a-0000e447-933d-8000-9baa-011c48011c48_62810"(
            numerator: sum(
              local!onboardingRequestsCompleteInMovingAverageRangeForDate.completionDays
            ),
            denominator: count(
              local!onboardingRequestsCompleteInMovingAverageRangeForDate
            ),
            default: 0
          ),
          1
        )
      )
    )
  ),
  local!showGrid: false(),
  local!categories: #"SYSTEM_SYSRULES_forEach"(
    items: local!onboardingRequestsCompletionDaysMovingAverageData.dateStamp,
    expression: #"_a-0000e447-933d-8000-9baa-011c48011c48_31679"(date: fv!item, user: loggedInUser())
  ),
  local!chartSeries: #"SYSTEM_SYSRULES_chartSeries"(
    label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
      i18nData: ri!i18nData,
      bundleKey: "AS.CO.AllBundles.lbl_Days"
    ),
    data: local!onboardingRequestsCompletionDaysMovingAverageData.avgDays
  ),
  {
    #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3312448"(
      "AS_IO_CPS_viewAverageDaysPerOnboarding"
    ),
    #"_a-0000e77d-004e-8000-9c08-011c48011c48_2838871"(
      header: #"SYSTEM_SYSRULES_sideBySideLayout"(
        items: {
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: #"SYSTEM_SYSRULES_richTextHeader"(
                text: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                  i18nData: ri!i18nData,
                  bundleKey: "AS.CO.AllBundles.lbl_AverageDaysPerOnboarding"
                ),
                size: "SMALL"
              )
            )
          ),
          /*Toggle*/
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"_a-0000e77d-004e-8000-9c08-011c48011c48_2835419"(
              showGrid: local!showGrid,
              i18nData: ri!i18nData
            ),
            width: "MINIMIZE"
          )
        }
      ),
      contents: {
        #"SYSTEM_SYSRULES_sectionLayout_v1"(
          label: "",
          contents: /*Grid*/
          {
            #"_a-0000e776-697a-8000-9c05-011c48011c48_2826556"(
              gridLabel: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                i18nData: ri!i18nData,
                bundleKey: "AS.CO.AllBundles.lbl_AverageDaysPerOnboarding"
              ),
              chartSeries: local!chartSeries,
              categories: local!categories,
              columnHeading: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                i18nData: ri!i18nData,
                bundleKey: "AS.CO.AllBundles.lbl_Date"
              ),
              showWhen: local!showGrid
            ),
            /*Chart*/
            #"SYSTEM_SYSRULES_lineChartField_v1"(
              labelPosition: "COLLAPSED",
              categories: local!categories,
              series: local!chartSeries,
              showLegend: false,
              connectNulls: true,
              showWhen: local!showGrid = false(),
              colorScheme: #"_a-0000e65f-acc5-8000-9bb8-011c48011c48_409039"(
                colorSchemeValue: #"_a-0000e62c-7375-8000-9bec-011c48011c48_1924695"(brandingKey: "ChartColorScheme"),
                returnRawHexCodes: false
              ),
              height: "MICRO",
              xAxisStyle: "NONE",
              yAxisStyle: "NONE"
            )
          },
          divider: "BELOW"
        ),
        /*KPIs*/
        #"SYSTEM_SYSRULES_columnsLayout"(
          columns: {
            #"SYSTEM_SYSRULES_columnLayout"(
              contents: a!localVariables(
                local!movingAverageValueForToday: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76162"(
                  array: local!onboardingRequestsCompletionDaysMovingAverageData
                ).avgDays,
                local!movingAverageValueForFirstDatapoint: local!onboardingRequestsCompletionDaysMovingAverageData[1].avgDays,
                #"_a-0000e607-97fd-8000-9be9-011c48011c48_1683101"(
                  differenceValue: local!movingAverageValueForToday - local!movingAverageValueForFirstDatapoint,
                  displayValue: local!movingAverageValueForToday,
                  showPercentageSymbol: false,
                  i18nData: ri!i18ndata
                )
              )
            )
          }
        )
      }
    )
  }
)
