interface.definition:
a!localVariables(
  local!docRequirementUploadTypes: #"_a-0000e79b-0335-8000-9c0c-011c48011c48_3043295"(i18nData: ri!i18nData),
  local!selectedUploadField: #"_a-0000e79b-0335-8000-9c0c-011c48011c48_3043388",
  local!dueDate: if(
    ri!onboarding.slaDayType = #"_a-0000e460-9d93-8000-9bc1-011c48011c48_620378",
    workday(
      today(),
      #"_a-0000e79b-0335-8000-9c0c-011c48011c48_3039343"
    ),
    today() + #"_a-0000e79b-0335-8000-9c0c-011c48011c48_3039343"
  ),
  #"SYSTEM_SYSRULES_cardLayout"(
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_74506"(
                label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
                  i18nData: ri!i18nData,
                  bundleKey: "AS.FS.AllBundles.lbl_Upload"
                ),
                choiceLabels: local!docRequirementUploadTypes.choiceLabel,
                choiceValues: local!docRequirementUploadTypes.choiceValue,
                value: local!selectedUploadField,
                validations: {},
                saveInto: {
                  local!selectedUploadField,
                  /*update task fields*/
                  #"_a-0000e79b-0335-8000-9c0c-011c48011c48_3044918"(
                    selectedUploadField: local!selectedUploadField,
                    docUploadTask: ri!docUploadTasks[ri!taskIndex],
                    reviewTask: ri!reviewTasks[ri!taskIndex],
                    dueDate: local!dueDate,
                    dueDateCalculationUnit: ri!onboarding.slaDayType
                  ),
                  if(
                    local!selectedUploadField = #"_a-0000e79b-0335-8000-9c0c-011c48011c48_3043381",
                    {
                      /*populate requirement with AS_IO_RequiredOnboardingDocument*/
                      a!save(
                        ri!allDocumentRequirements[wherecontains(
                          ri!requiredDocument.requirementName, ri!allDocumentRequirements.requirementName
                        )].onboardingDocument,
                        'type!{urn:com:appian:types:AS:IO}AS_IO_OnboardingDocument'(onboardingDocumentId: null)
                      ),
                      a!save(
                        ri!docUploadTasks[ri!taskIndex].requiredDocument.onboardingDocument,
                        'type!{urn:com:appian:types:AS:IO}AS_IO_OnboardingDocument'(onboardingDocumentId: null)
                      )
                    },
                    {
                      /*null out requirement's OnboardingDocument*/
                      a!save(
                        ri!allDocumentRequirements[wherecontains(
                          ri!requiredDocument.requirementName, ri!allDocumentRequirements.requirementName
                        )].onboardingDocument,
                        null
                      ),
                      a!save(
                        ri!docUploadTasks[ri!taskIndex].requiredDocument.onboardingDocument,
                        null
                      )
                    }
                  ),
                  if(
                    local!selectedUploadField = #"_a-0000e79b-0335-8000-9c0c-011c48011c48_3043381",
                    a!save(
                      ri!requiredDocument.onboardingDocument,
                      null
                    ),
                    a!save(
                      ri!requiredDocument.onboardingDocument,
                      'type!{urn:com:appian:types:AS:IO}AS_IO_OnboardingDocument'(onboardingDocumentId: null)
                    )
                  )
                },
                labelPosition: "ABOVE",
                choiceLayout: "COMPACT",
                choiceStyle: "CARDS"
              )
            }
          )
        }
      ),
      choose(
        #"_a-0000e40a-de32-8000-9ba8-011c48011c48_280802"(
          value: local!selectedUploadField,
          inArray: {
            #"_a-0000e79b-0335-8000-9c0c-011c48011c48_3043381",
            #"_a-0000e79b-0335-8000-9c0c-011c48011c48_3043388"
          }
        ),
        {},
        #"_a-0000e79b-0335-8000-9c0c-011c48011c48_3042233"(
          i18nData: ri!i18nData,
          requiredDocument: ri!requiredDocument,
          allUploadedDocuments: ri!allUploadedDocuments,
          onboardingRequest: ri!onboarding,
          mappedCustomerDocumentIds: ri!mappedCustomerDocumentIds,
          allDocumentRequirements: ri!allDocumentRequirements,
          reviewTasks: ri!reviewTasks,
          taskIndex: ri!taskIndex,
          markRequiredFields: local!selectedUploadField = #"_a-0000e79b-0335-8000-9c0c-011c48011c48_3043381"
        ),
        #"_a-0000e79b-0335-8000-9c0c-011c48011c48_3044845"(
          i18nData: ri!i18nData,
          docUploadTasks: ri!docUploadTasks,
          onboardingRequest: ri!onboarding,
          reviewTasks: ri!reviewTasks,
          taskIndex: ri!taskIndex,
          markRequiredFields: local!selectedUploadField = #"_a-0000e79b-0335-8000-9c0c-011c48011c48_3043388"
        )
      )
    },
    showWhen: ri!isExpanded
  )
)
