interface.definition:
a!localVariables(
  local!newRequiredDocs: #"_a-0000e3d1-f4dc-8000-9ba4-011c48011c48_143475"(
    type: 'type!{urn:com:appian:types:AS:IO}AS_IO_T_RequiredDocument'
  ),
  local!requiredDocsToBeAdded,
  local!requiredDocsToBeDeleted,
  local!attachReqDocsTasks,
  local!reviewReqDocsTasks,
  #"_a-0000e3d1-f4dc-8000-9ba4-011c48011c48_134344"(
    additionalSecondaryButtons: #"SYSTEM_SYSRULES_ButtonWidget"(
      label: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
        i18nData: ri!i18nData,
        bundleKey: "AS.CO.AllBundles.btn_Back"
      ),
      saveInto: {
        a!save(ri!activeStep, ri!activeStep - 1),
        a!save(
          ri!userAction,
          #"_a-0000e50d-bf1d-8000-9bcd-011c48011c48_1014889"
        )
      },
      showWhen: ri!activeStep <> #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3247325",
      disabled: ri!isBackDisabled
    ),
    confirmLabel: if(
      ri!activeStep = #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3247351",
      if(
        ri!readOnly,
        #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
          i18nData: ri!i18nData,
          bundleKey: "AS.CO.AllBundles.btn_Close"
        ),
        if(
          #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(ri!editedTemplate.templateId),
          #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
            i18nData: ri!i18nData,
            bundleKey: "AS.CO.AllBundles.btn_Submit"
          ),
          #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
            i18nData: ri!i18nData,
            bundleKey: "AS.CO.AllBundles.btn_Create"
          )
        )
      ),
      #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
        i18nData: ri!i18nData,
        bundleKey: "AS.CO.AllBundles.btn_Next"
      )
    ),
    cancelLabel: if(
      ri!readOnly,
      #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
        i18nData: ri!i18nData,
        bundleKey: "AS.CO.AllBundles.btn_Close"
      ),
      #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_81818"(
        i18nData: ri!i18nData,
        bundleKey: "AS.CO.AllBundles.btn_Cancel"
      )
    ),
    confirmSaveInto: {
      if(
        ri!activeStep = #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3247325",
        {
          if(
            ri!previousOnboardingTypeCode = ri!editedTemplate.onboardingTypeCode,
            {},
            /*if the type code has changed, update the products*/
            {
              a!save(
                ri!editedTemplate.onboardingProductTemplates,
                #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3295423"(
                  onboardingTypeCode: ri!editedTemplate.onboardingTypeCode,
                  templateId: ri!editedTemplate.templateId
                )
              ),
              /*update the "previous" type code*/
              a!save(
                ri!previousOnboardingTypeCode,
                ri!editedTemplate.onboardingTypeCode
              )
            }
          ),
          /*update isNewDefaultTemplate to tell the PM that a new default has been established*/
          a!save(
            ri!isNewDefaultTemplate,
            and(
              ri!editedTemplate.isDefault,
              #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75636"(ri!oldTemplate.isDefault)
            )
          )
        },
        if(
          ri!activeStep = #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3247343",
          {
            a!save(
              local!newRequiredDocs,
              #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3288118"(
                oldRequiredDocumentTemplates: ri!editedTemplate.requiredDocumentTemplates,
                newRequiredDocTemplateMap: ri!currentRequiredDocTemplateMap,
                templateId: ri!editedTemplate.templateId
              )
            ),
            {
              /*Required Docs added newly in this flow to construct tasks*/
              a!save(
                local!requiredDocsToBeAdded,
                difference(
                  local!newRequiredDocs,
                  ri!editedTemplate.requiredDocumentTemplates
                )
              ),
              /*Required Docs removed in this flow to remove tasks*/
              a!save(
                local!requiredDocsToBeDeleted,
                difference(
                  ri!editedTemplate.requiredDocumentTemplates,
                  local!newRequiredDocs
                )
              ),
              a!save(
                ri!editedTemplate.requiredDocumentTemplates,
                reject(
                  #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536",
                  { local!newRequiredDocs }
                )
              ),
              /*Constructing tasks only for the newly added required docs*/
              a!save(
                local!attachReqDocsTasks,
                #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3326661"(
                  newRequiredDocuments: reject(
                    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536",
                    { local!requiredDocsToBeAdded }
                  ),
                  i18ndata: ri!i18nData,
                  allRequiredDocs: ri!editedTemplate.requiredDocumentTemplates,
                  /*existingTemplateTasks: ri!editedTemplate.templateTasks*/

                )
              ),
              a!save(
                local!reviewReqDocsTasks,
                #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3326815"(
                  newRequiredDocuments: reject(
                    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536",
                    { local!requiredDocsToBeAdded }
                  ),
                  i18ndata: ri!i18nData,
                  attachRequiredDocsTasks: local!attachReqDocsTasks,
                  allRequiredDocs: ri!editedTemplate.requiredDocumentTemplates
                )
              ),
              /*save attach and review tasks for new required docs*/
              a!save(
                ri!editedTemplate.templateTasks,
                append(
                  ri!editedTemplate.templateTasks,
                  /*Sorts the tasks based on requiredDocumentTemplateUUID so that the each attach and review task are next to each other */
                  #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75708"(
                    cdt: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76367"(
                      {
                        local!attachReqDocsTasks,
                        local!reviewReqDocsTasks
                      }
                    ),
                    field: "requiredDocumentTemplateUUID",
                    array: ri!editedTemplate.requiredDocumentTemplates.requiredDocumentTemplateUUID
                  )
                )
              ),
              /*Removing attach and review tasks of removed Required documents*/
              #"_a-0000e3d1-f4dc-8000-9ba4-011c48011c48_143273"(
                selectedTemplateTasks: ri!editedTemplate.templateTasks,
                taskUUIDsToRemove: index(
                  index(
                    ri!editedTemplate.templateTasks,
                    wherecontains(
                      touniformstring(
                        index(
                          local!requiredDocsToBeDeleted,
                          "requiredDocumentTemplateUUID",
                          null
                        )
                      ),
                      touniformstring(
                        ri!editedTemplate.templateTasks.requiredDocumentTemplateUUID
                      )
                    ),
                    null
                  ),
                  "templateTaskUUID",
                  null
                )
              )
            }
          },
          if(
            ri!activeStep = #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3247351",
            {
              a!save(ri!finalTemplate, ri!editedTemplate),
              a!save(
                ri!userAction,
                #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73305"
              ),
              #"_a-0000e825-b425-8000-9c1c-011c48011c48_3643407"()
            },
            {}
          )
        )
      ),
      a!save(ri!activeStep, ri!activeStep + 1)
    },
    cancelSaveInto: a!save(
      ri!userAction,
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73173"
    ),
    includeDivider: true,
    confirmSubmit: ri!activeStep = #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3247351",
    confirmDisabled: ri!confirmDisabled,
    cancelDisabled: ri!cancelDisabled,
    cancelStyle: "LINK",
    cancelSubmit: true,
    validate: true,
    hideCancel: and(
      ri!readOnly,
      ri!activeStep = #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3247351"
    ),
    showWhen: ri!showWhen
  )
)
