rule.definition:
if(
  #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!newRequiredDocTemplateMap),
  #"_a-0000e3d1-f4dc-8000-9ba4-011c48011c48_143475"(
    type: 'type!{urn:com:appian:types:AS:IO}AS_IO_T_RequiredDocument'
  ),
  a!localVariables(
    /*Sorting the current templates such that the latest ones are at the end so they can be removed first*/
    local!sortedTemplates: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75720"(
      cdt: ri!oldRequiredDocumentTemplates,
      field: "requiredDocumentTemplateId",
      ascending: false()
    ),
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: ri!newRequiredDocTemplateMap,
        expression: a!localVariables(
          local!currentTemplates: index(
            local!sortedTemplates,
            wherecontains(
              tostring(fv!item.docTypeCode),
              touniformstring({ local!sortedTemplates.docTypeCode })
            ),
            null
          ),
          if(
            #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(fv!item.docTypeCode),
            local!currentTemplates,
            if(
              /*If there's no chnage in quantity, then the existing list of AS_IO_T_RequiredDocument remains*/
              fv!item.quantity = length({ local!currentTemplates }),
              local!currentTemplates,
              /*If the new quantity is more than the existing, then for each extra new quantity, a row of AS_IO_T_RequiredDocument is appended to the existing*/
              fv!item.quantity > length({ local!currentTemplates }),
              a!localVariables(
                local!docTypeCode: fv!item.docTypeCode,
                append(
                  local!currentTemplates,
                  #"SYSTEM_SYSRULES_forEach"(
                    items: enumerate(
                      fv!item.quantity - length({ local!currentTemplates })
                    ),
                    expression: 'type!{urn:com:appian:types:AS:IO}AS_IO_T_RequiredDocument'(
                      docTypeCode: local!docTypeCode,
                      templateId: ri!templateId,
                      requiredDocumentTemplateUUID: #"_a-0000e7e5-dad4-8000-9c14-011c48011c48_3308181"()
                    )
                  )
                )
              ),
              /*If the new quantity is less than the existing, then for each extra old quantity, the latest row of AS_IO_T_RequiredDocument is removed*/
              remove(
                local!currentTemplates,
                {
                  enumerate(
                    length({ local!currentTemplates }) - fv!item.quantity
                  ) + 1
                }
              )
            )
          )
        )
      )
    )
  )
)

