rule.definition:
/*Products are queried, constructed and updated for Audit Purposes*/
/*For teh account Types added and deleted, accounts are written and deleted from DB*/
if(
  and(
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!accountTypesAdded),
    #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_73536"(ri!accountTypesDeleted)
  ),
  {},
  a!localVariables(
    /*Query AS_IO_T_OnboardingProduct of the same product type */
    local!TProductsOfTheType: #"_a-0000e7fa-f829-8000-9c17-011c48011c48_3364087"(
      productTypeCode: ri!productTypeCode,
      executeWhen: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(ri!productTypeCode),
      returnType: #"_a-0000e415-3035-8000-9bb2-011c48011c48_290163"
    ),
    /* This is queried because we want to know how many non deleted templates we have for the T_OnboardingProduct */
    local!templatesOfProductType: #"_a-0000e2cd-bc96-8000-9ba2-011c48011c48_155202"(
      excludeNestedCdts: true(),
      templateId: local!TProductsOfTheType.templateId,
      isDeleted: false(),
      executeWhen: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(local!TProductsOfTheType.templateId),
      returnType: #"_a-0000e415-3035-8000-9bb2-011c48011c48_290163",
      isActive: { true(), false() }
    ),
    /*Updating templates with the AS_IO_T_OnboardingProduct*/
    local!originalTemplates: #"SYSTEM_SYSRULES_forEach"(
      items: local!templatesOfProductType,
      expression: #"_a-0000e40a-de32-8000-9ba8-011c48011c48_260165"(
        cdt: fv!item,
        fields: "onboardingProductTemplates",
        newValues: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
          cdt: local!TProductsOfTheType,
          field: "templateId",
          value: fv!item.templateId
        )
      )
    ),
    /*Updating products with the new account types*/
    local!updatedProductsWithNewAccs: if(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(ri!accountTypesAdded),
      #"SYSTEM_SYSRULES_forEach"(
        items: local!TProductsOfTheType,
        expression: a!localVariables(
          local!currentProduct: fv!item,
          /*the constructed accountType is added to the existing account types of the product*/
          #"_a-0000e40a-de32-8000-9ba8-011c48011c48_269170"(
            cdt: local!currentProduct,
            fieldValuePairs: {
              field: "accountTypeProductTypeMappings",
              value: a!flatten(
                append(
                  local!currentProduct.accountTypeProductTypeMappings,
                  #"SYSTEM_SYSRULES_forEach"(
                    items: ri!accountTypesAdded,
                    expression: if(
                      /*Checks if the account type is already present for the product*/
                      contains(
                        touniformstring(
                          index(
                            local!currentProduct.accountTypeProductTypeMappings,
                            "accountTypeCode",
                            null
                          )
                        ),
                        touniformstring(fv!item)
                      ),
                      /*If present, no changes*/
                      {},
                      /*If not, the accountType is constructed*/
                      'type!{urn:com:appian:types:AS:IO}AS_IO_T_OnboardingAccountTypeProductTypeMapping'(
                        accountTypeCode: fv!item,
                        onboardingProductTemplateId: local!currentProduct.onboardingProductTemplateId
                      )
                    )
                  )
                )
              )
            }
          )
        )
      ),
      local!TProductsOfTheType
    ),
    /*Removing the deleted account types and updating the updated product list*/
    local!updatedProductsWithNewAndDelAccs: if(
      #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75782"(ri!accountTypesDeleted),
      #"SYSTEM_SYSRULES_forEach"(
        items: local!updatedProductsWithNewAccs,
        expression: #"_a-0000e40a-de32-8000-9ba8-011c48011c48_269170"(
          cdt: fv!item,
          fieldValuePairs: {
            field: "accountTypeProductTypeMappings",
            value: a!flatten(
              #"_a-0000e40a-de32-8000-9ba8-011c48011c48_271202"(
                cdt: fv!item.accountTypeProductTypeMappings,
                field: "accountTypeCode",
                value: ri!accountTypesDeleted
              )
            )
          }
        )
      ),
      local!updatedProductsWithNewAccs
    ),
    /*Updating templates with the updated products AS_IO_T_OnboardingProduct*/
    local!newTemplates: #"SYSTEM_SYSRULES_forEach"(
      items: local!templatesOfProductType,
      expression: #"_a-0000e40a-de32-8000-9ba8-011c48011c48_260165"(
        cdt: fv!item,
        fields: "onboardingProductTemplates",
        newValues: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
          cdt: local!updatedProductsWithNewAndDelAccs,
          field: "templateId",
          value: fv!item.templateId
        )
      )
    ),
    /*Indexing out the all the accounts from products that are updated with newly added accounts*/
    local!accountTemplates: a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!updatedProductsWithNewAccs,
        expression: fv!item.accountTypeProductTypeMappings
      )
    ),
    /*need only newly added accounts, so filtering out using pk*/
    local!accountTemplatesToWrite: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
      cdt: local!accountTemplates,
      field: "onboardingAccountTypeProductTypeMappingId",
      value: null
    ),
    /*using account templates local itself as it contains all accounts before deletion.*/
    local!accountTemplateIdsToDelete: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
      cdt: local!accountTemplates,
      field: "accountTypeCode",
      value: ri!accountTypesDeleted
    ).onboardingAccountTypeProductTypeMappingId,
    a!map(
      originalOnboardingTemplates: local!originalTemplates,
      newOnboardingTemplates: local!newTemplates,
      accountTemplatesToWrite: local!accountTemplatesToWrite,
      accountTemplateIdsToDelete: local!accountTemplateIdsToDelete
    )
  )
)
