rule.definition:
a!flatten(
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!oldRejectedTasks,
    expression: a!localVariables(
      local!rejectedTask: fv!item,
      /*Get task Ids of tasks with these review tasks as precedents*/
      local!dependentTaskIds: #"_a-0000e3c8-6861-8000-9ba2-011c48011c48_115660"(
        taskId_precedent: local!rejectedTask.taskId
      ).taskId,
      /*Remove the new attach doc task which is also a dependent of the rejected review task*/
      /*Filter out all the document upload tasks as they can't have any other precedents*/
      local!allDependentTasks:  #"_a-0000e393-fbf1-8000-9ba2-011c48011c48_104148"(
        returnType: #"_a-0000e415-3035-8000-9bb2-011c48011c48_290163",
        taskId: local!dependentTaskIds
      ),
      /*   remove newly created doc upload tasks from the list   */
      local!removeDocUploadTasks: remove(
        local!allDependentTasks,
        where(
/*       same required document      */
            #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76433"(
              cdt: local!allDependentTasks,
              field: "requiredDocument.requiredOnboardingDocumentId",
              indexDefault: 0
            ) = 
            #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76433"(
              cdt: local!rejectedTask,
              field: "requiredDocument.requiredOnboardingDocumentId",
              indexDefault: 0
            ),
          )
      ),
      #"SYSTEM_SYSRULES_forEach"(
        items: local!removeDocUploadTasks,
        expression: a!localVariables(
          /*with the help of old precedent task id, filter out the old review task from the list of all task*/
          /*Use the required document Id of this task to filter out the corresponding new review task*/
          local!newReviewTask: #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_75800"(
            cdt: ri!newReviewTasks,
            field: "requiredDocument.requiredOnboardingDocumentId",
            value: local!rejectedTask.requiredDocument.requiredOnboardingDocumentId
          ),
          local!removedOldTaskPrecedentList: remove(
            #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76433"(
              cdt: fv!item,
              field: "taskPrecedents",
              indexDefault: {}
            ),
            where(
              #"_a-0000e2ad-62e7-8000-9ba2-011c48011c48_76433"(
                cdt: fv!item,
                field: "taskPrecedents.taskId_precedent",
                indexDefault: {}
              ) = local!rejectedTask.taskId,

            )
          ),
          /*Update this new review task's Id as precedent task id*/
          local!precedentsListWithAddedTask: append(
            local!removedOldTaskPrecedentList,
            'type!{urn:com:appian:types:AS:IO}AS_IO_Task_Precedent'(
              taskId: fv!item.taskId,
              taskId_precedent: local!newReviewTask.taskId
            )
          ),
          #"_a-0000e40a-de32-8000-9ba8-011c48011c48_269170"(
            cdt: fv!item,
            fieldValuePairs: {
              field: "taskPrecedents",
              value: local!precedentsListWithAddedTask
            }
          )
        )
      ),

    )
  )
)
