rule.definition:
{
  /*update task precedents*/
  /*this is done first to prevent breaking on deletion of the last task*/
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!tasksForRequest,
    expression: {
      a!save(
        fv!item.taskPrecedents,
        #"SYSTEM_SYSRULES_forEach"(
          items: fv!item.taskPrecedents,
          expression: if(
            contains(
              ri!taskIndexesToRemove,
              fv!item.taskId_precedent
            ),
            /*Where index is equal to that which was removed, remove it*/
            {},
            /*decrement precedent index by the number of removed indexes less than it*/
            #"_a-0000e40a-de32-8000-9ba8-011c48011c48_260165"(
              cdt: fv!item,
              fields: "taskId_precedent",
              newValues: fv!item.taskId_precedent - count(
                where(
                  ri!taskIndexesToRemove < fv!item.taskId_precedent
                )
              )
            )
          )
        )
      )
    }
  ),
  /*remove tasks from list*/
  a!save(
    ri!tasksForRequest,
    remove(
      ri!tasksForRequest,
      ri!taskIndexesToRemove
    )
  )
}
